class Solution {
public:
    int dp[33][33][33][33];
    int minimumSum(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        for(int i=0;i<n;++i){
            for(int j=0;j<m;++j){
                for(int ii=i;ii<n;++ii){
                    for(int jj=j;jj<m;++jj){
                        int a=-1,b=1e9,c=-1,d=1e9;
                        for(int ik=i;ik<=ii;++ik){
                            for(int jk=j;jk<=jj;++jk){
                                if(grid[ik][jk]==1){
                                    a=max(a,ik);
                                    b=min(b,ik);
                                    c=max(c,jk);
                                    d=min(d,jk);
                                }
                            }
                        }
                        if(a==-1&&c==-1) dp[i][j][ii][jj]=0;
                        else dp[i][j][ii][jj]=(a-b+1)*(c-d+1);
                    }
                }
            }
        }
        int ans=1e9;
        for(int i=0;i<n-1;++i){
            for(int ii=i+1;ii+1<n;++ii){
                ans=min(ans,dp[0][0][i][m-1]+dp[i+1][0][ii][m-1]+dp[ii+1][0][n-1][m-1]);
            }
        }
        for(int j=0;j<m-1;++j){
            for(int jj=j+1;jj+1<m;++jj){
                ans=min(ans,dp[0][0][n-1][j]+dp[0][j+1][n-1][jj]+dp[0][jj+1][n-1][m-1]);
            }
        }
        for(int i=0;i<n;++i){
            for(int j=0;j<m;++j){
                ans=min(ans,dp[0][0][i][j]+dp[i+1][0][n-1][j]+dp[0][j+1][n-1][m-1]);
                ans=min(ans,dp[0][0][i][j]+dp[0][j+1][i][m-1]+dp[i+1][0][n-1][m-1]);
                if(j>=1&&i>=1){
                    ans=min(ans,dp[i][j][n-1][m-1]+dp[i][0][n-1][j-1]+dp[0][0][i-1][m-1]);
                    ans=min(ans,dp[i][j][n-1][m-1]+dp[0][j][i-1][m-1]+dp[0][0][n-1][j-1]);
                }
            }
        }
        // cout<<dp[2][1][3][2]+dp[2][0][3][0]<<"..\n";
        return ans;
    }
};
