class Solution {
public:
    int minimumSum(vector<vector<int>>& a) {
        int n = a.size(), m = a[0].size();

        auto cal = [a] (const int l, const int r, const int u, const int d) {
            int lc = INT_MAX, rc = -1, uc = INT_MAX, dc = -1;
            bool ok = false;
            for (int i = l; i <= r; i++) {
                for (int j = u; j <= d; j++) {
                    if (a[i][j] == 1) {
                        lc = min(lc, i);
                        rc = max(rc, i);
                        uc = min(uc, j);
                        dc = max(dc, j);
                        ok = true;
                    }
                }
            }
            if (!ok) {
                return (long long) INT_MAX;
            }
            return (long long) (rc - lc + 1) * (dc - uc + 1);
        };

        long long ans = INT_MAX;
        if (n >= 2 && m >= 2) {
            for (int i = 0; i < n - 1; i++) {
                for (int j = 0; j < m - 1; j++) {
                    ans = min(ans, cal(0, i, 0, j) + cal(0, i, j + 1, m - 1) + cal(i + 1, n - 1, 0, m - 1));
                    // cout << ans << endl;
                    ans = min(ans, cal(0, i, 0, m - 1) + cal(i + 1, n - 1, 0, j) + cal(i + 1, n - 1, j + 1, m - 1));
                    // cout << ans << endl;
                }
            }
        // cout << "===" << endl;
            for (int i = 0; i < n - 1; i++) {
                for (int j = 0; j < m - 1; j++) {
                    ans = min(ans, cal(0, i, 0, j) + cal(i + 1, n - 1, 0, j) + cal(0, n - 1, j + 1, m - 1));
                    // cout << ans << endl;
                    ans = min(ans, cal(0, n - 1, 0, j) + cal(0, i, j + 1, m - 1) + cal(i + 1, n - 1, j + 1, m - 1));
                    // cout << ans << endl;
                }
            }
        }
        // cout << "===" << endl;
        if (n >= 3) {
            for (int i = 0; i < n - 1; i++) {
                for (int j = i + 1; j < n - 1; j++) {
                    ans = min(ans, cal(0, i, 0, m - 1) + cal(i + 1, j, 0, m - 1) + cal(j + 1, n - 1, 0, m - 1));
                    // cout << ans << endl;
                }
            }
        }
        // cout << "===" << endl;
        if (m >= 3) {
            for (int i = 0; i < m - 1; i++) {
                for (int j = i + 1; j < m - 1; j++) {
                    ans = min(ans, cal(0, n - 1, 0, i) + cal(0, n - 1, i + 1, j) + cal(0, n - 1, j + 1, m - 1));
                    // cout << ans << endl;
                }
            }
        }
        // cout << "end" << endl;
        return (int) ans;
    }
};
