class Solution {
    int sol(vector<vector<int>>& grid, int X1, int X2, int Y1, int Y2) {
        int x1 = X2, x2 = X1, y1 = Y2, y2 = Y1;
        for(int i = X1; i <= X2; i++) {
            for(int j = Y1; j <= Y2; j++) {
                if(grid[i][j]) {
                    x1 = min(x1, i);
                    x2 = max(x2, i);
                    y1 = min(y1, j);
                    y2 = max(y2, j);
                }
            }
        }
        if(x2 < x1 || y2 < y1) {
            return 0;
        }
        // cout<<X1<<" "<<X2<<" "<<Y1<<" "<<Y2<<"\n";
        // cout<<x1<<" "<<x2<<" "<<y1<<" "<<y2<<"\n";
        return (x2 - x1 + 1) * (y2 - y1 + 1);
    }

public:
    int minimumSum(vector<vector<int>>& grid) {
        int ans = INT_MAX, n = grid.size(), m = grid[0].size();
        for(int i = 0; i < n - 1; i++) {
            for(int j = i + 1; j < n - 1; j++) {
                ans = min(ans, sol(grid, 0, i, 0, m - 1) + sol(grid, i + 1, j, 0, m - 1) + sol(grid, j + 1, n - 1, 0, m - 1));
            }
        }
        for(int i = 0; i < m - 1; i++) {
            for(int j = i + 1; j < m - 1; j++) {
                ans = min(ans, sol(grid, 0, n - 1, 0, i) + sol(grid, 0, n - 1, i + 1, j) + sol(grid, 0, n - 1, j + 1, m - 1));
            }
        }
        for(int i = 0; i < n - 1; i++) {
            for(int j = 0; j < m - 1; j++) {
                ans = min(ans, sol(grid, 0, n - 1, 0, j) + sol(grid, 0, i, j + 1, m - 1) + sol(grid, i + 1, n - 1, j + 1, m - 1));
                ans = min(ans, sol(grid, 0, n - 1, j + 1, m - 1) + sol(grid, 0, i, 0, j) + sol(grid, i + 1, n - 1, 0, j));
                ans = min(ans, sol(grid, 0, i, 0, m - 1) + sol(grid, i + 1, n - 1, 0, j) + sol(grid, i + 1, n - 1, j + 1, m - 1));
                ans = min(ans, sol(grid, i + 1, n - 1, 0, m - 1) + sol(grid, 0, i, 0, j) + sol(grid, 0, i, j + 1, m - 1));
            }
        }
        return ans;
    }
};
