class Solution:
    def minimumSum(self, mat: List[List[int]]) -> int:
        m, n = len(mat), len(mat[0])
        acc = [[0] * (n + 1) for _ in range(m + 1)]
        for i in range(m):
            for j in range(n):
                acc[i][j] = mat[i][j] + acc[i][j - 1]
            for j in range(n):
                acc[i][j] += acc[i - 1][j]
        # print(acc)

        def area(i0, i1, j0, j1):
            if acc[i0 - 1][j0 - 1] == acc[i1][j1]:
                return 0
            mir = inf
            mar = 0
            mic = inf
            mac = 0
            for i in range(i0, i1 + 1):
                for j in range(j0, j1 + 1):
                    if mat[i][j]:
                        mir = min(i, mir)
                        mar = max(i, mar)
                        mic = min(j, mic)
                        mac = max(j, mac)
            return (mac - mic + 1) * (mar - mir + 1)

        ans = inf

        # hcut
        for i in range(m - 2):
            at1 = area(0, i, 0, n - 1)
            if at1 and at1 < ans:
                for ii in range(i + 1, m - 1):
                    at2 = area(i + 1, ii, 0, n - 1)
                    if at2 and at1 + at2 < ans:
                        at3 = area(ii + 1, m - 1, 0, n - 1)
                        if at3:
                            # print('i', i, 'ii', ii, 'at', at1, at2, at3)
                            ans = min(ans, at1 + at2 + at3)


        for i in range(m - 1):
            atop = area(0, i, 0, n - 1)
            if atop and atop < ans:
                for j in range(n - 1):
                    aleft = area(i + 1, m - 1, 0, j)
                    if aleft and atop + aleft < ans:
                        aright = area(i + 1, m - 1, j + 1, n - 1)
                        if aright:
                            ans = min(ans, atop + aleft + aright)
            abot = area(i + 1, m - 1, 0, n - 1)
            if abot and abot < ans:
                for j in range(n - 1):
                    aleft = area(0, i, 0, j)
                    if aleft and abot + aleft < ans:
                        aright = area(0, i, j + 1, n - 1)
                        if aright:
                            ans = min(ans, abot + aleft + aright)

        # vcut
        for j in range(n - 2):
            at1 = area(0, m - 1, 0, j)
            if at1 and at1 < ans:
                for jj in range(j + 1, n - 1):
                    at2 = area(0, m - 1, j + 1, jj)
                    if at2 and at1 + at2 < ans:
                        at3 = area(0, m - 1, jj + 1, n - 1)
                        if at3:
                            ans = min(ans, at1 + at2 + at3)

        for j in range(n - 1):
            aleft = area(0, m - 1, 0, j)
            if aleft and aleft < ans:
                for i in range(m - 1):
                    atop = area(0, i, j + 1, n - 1)
                    if atop and aleft + atop < ans:
                        abot = area(i + 1, m - 1, j + 1, n - 1)
                        if abot:
                            ans = min(ans, aleft + atop + abot)
            aright = area(0, m - 1, j + 1, n - 1)
            if aright and aright < ans:
                for i in range(m - 1):
                    atop = area(0, i, 0, j)
                    if atop and aright + atop < ans:
                        abot = area(i + 1, m - 1, 0, j)
                        if abot:
                            ans = min(ans, aright + atop + abot)

        return ans
