class Solution {
    public int minimumSum(int[][] grid) {
        int rows = grid.length,colms = grid[0].length;
        int ans=Integer.MAX_VALUE;
        for(int i=0;i<rows;i++){
            for(int j=0;j<colms;j++){
                int rec1 = minimumArea(grid,0,0,rows-1,j-1) + minimumArea(grid,0,j,i-1,colms-1) + minimumArea(grid,i,j,rows-1,colms-1);
                int rec2 = minimumArea(grid,0,0,i-1,j-1) + minimumArea(grid,0,j,i-1,colms-1) + minimumArea(grid,i,0,rows-1,colms-1);
                int rec3 = minimumArea(grid,0,0,i-1,j-1) + minimumArea(grid,i,0,rows-1,j-1) + minimumArea(grid,0,j,rows-1,colms-1);
                int rec4 = minimumArea(grid,0,0,i-1,colms-1) + minimumArea(grid,i,0,rows-1,j-1) + minimumArea(grid,i,j,rows-1,colms-1);
                ans = Math.min(ans,Math.min(rec1,Math.min(rec2,Math.min(rec3,rec4))));
            }
        }

        for(int i=0;i<rows;i++){
            for(int j=i;j<rows;j++){
                int rec = minimumArea(grid,0,0,i-1,colms-1)
                + minimumArea(grid,i,0,j-1,colms-1)
                + minimumArea(grid,j,0,rows-1,colms-1);
                ans = Math.min(ans,rec);
            }
        }
        for(int i=0;i<colms;i++){
            for(int j=i;j<colms;j++){
                int rec = minimumArea(grid,0,0,rows-1,i-1)
                + minimumArea(grid,0,i,rows-1,j-1)
                + minimumArea(grid,0,j,rows-1,colms-1);
                ans = Math.min(ans,rec);
            }
        }

        return ans;
    }

    public int minimumArea(int[][] grid,int startrow,int startcolm,int endrow,int endcolm) {
        if(endrow < 0 || endcolm <0 || startrow > endrow || startcolm > endcolm) return 0;
        int rows = endrow+1,colms = endcolm+1;
        int minrow=rows,mincolm = colms,maxrow = -1,maxcolm = -1;

        for(int row = startrow;row <rows;row++){
            for(int colm = startcolm;colm < colms;colm++){
                if(grid[row][colm] == 1){
                    minrow = Math.min(minrow,row);
                    maxrow = Math.max(maxrow,row);
                    mincolm = Math.min(mincolm,colm);
                    maxcolm = Math.max(maxcolm,colm);
                }
            }
        }

        if(minrow == rows)
            return 0;
        return (maxrow-minrow+1)*(1+maxcolm-mincolm);
    }
}
