class Solution:
    def minimumSum(self, grid: List[List[int]]) -> int:



        def cal_s(mny, mxy, mnx, mxx) -> int:
            u = 10000
            d = 0
            l = 10000
            r = 0
            key = False


            for y in range(mny, mxy+1):
                for x in range(mnx, mxx+1):
                    if grid[y][x] == 1:
                        key = True
                        u = min(u, y)
                        d = max(d, y)
                        l = min(l, x)
                        r = max(r, x)
            if key:
                return (d-u+1) * (r-l+1)
            return 0

        ny = len(grid)
        nx = len(grid[0])
        ans = nx * ny
        for lx in range(nx-1):
            sl = cal_s(0, ny-1, 0, lx)
            sr = cal_s(0, ny-1, lx+1, nx-1)
            if sl == 0 or sr == 0:
                continue
            if lx > 0:
                for lx2 in range(lx):
                    sl1 = cal_s(0, ny-1, 0, lx2)
                    sl2 = cal_s(0, ny-1, lx2+1, lx)
                    if sl1 == 0 or sl2 == 0:
                        continue
                    ans = min(ans, sl1+sl2+sr)
            if lx < nx-2:
                for lx2 in range(lx+1, nx-1):
                    sr1 = cal_s(0, ny-1, lx+1, lx2)
                    sr2 = cal_s(0, ny-1, lx2+1, nx-1)
                    if sr1 == 0 or sr2 == 0:
                        continue
                    ans = min(ans, sr1+sr2+sl)
            for uy in range(ny-1):
                sul1 = cal_s(0, uy, 0, lx)
                sul2 = cal_s(uy+1, ny-1, 0, lx)
                if sul1 != 0 and sul2 != 0:
                    ans = min(ans, sul1+sul2+sr)
                sur1 = cal_s(0, uy, lx+1, nx-1)
                sur2 = cal_s(uy+1, ny-1, lx+1, nx-1)
                if sur1 != 0 and sur2 != 0:
                    ans = min(ans, sur1+sur2+sl)

        for uy in range(ny-1):
            su = cal_s(0, uy, 0, nx-1)
            sd = cal_s(uy+1, ny-1, 0, nx-1)
            if su == 0 or sd == 0:
                continue
            if uy > 0:
                for uy2 in range(uy):
                    su1 = cal_s(0, uy2, 0, nx-1)
                    su2 = cal_s(uy2+1, uy, 0, nx-1)
                    if su1 == 0 or su2 == 0:
                        continue
                    ans = min(ans, su2+su1+sd)
            if uy < ny-2:
                for uy2 in range(uy+1, ny-1):
                    sd1 = cal_s(uy+1, uy2, 0, nx-1)
                    sd2 = cal_s(uy2, ny-1, 0, nx-1)
                    if sd1 == 0 or sd2 == 0:
                        continue
                    ans = min(ans, sd1+sd2+su)
            for lx in range(nx-1):
                slu1 = cal_s(0, uy, 0, lx)
                slu2 = cal_s(0, uy, lx+1, nx-1)
                if slu1 != 0 and slu2 != 0:
                    ans = min(ans, slu1+slu2+sd)
                sld1 = cal_s(uy+1, ny-1, 0, lx)
                sld2 = cal_s(uy+1, ny-1, lx+1, nx-1)
                if sld1 != 0 and sld2 != 0:
                    ans = min(ans, sld1+sld2+su)

        return ans
