class Solution {
public:
    int minimumSum(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();

        auto solve=[&](int startRow,int endRow,int startCol,int endCol){
            int minX=1e9;
            int minY=1e9;
            int maxX=0;
            int maxY=0;
            for(int i=startRow;i<=std::min(n-1,endRow);i++)
            {
                for(int j=startCol;j<=std::min(endCol,m-1);j++)
                {
                    if(grid[i][j])
                    {
                        minX=std::min(minX,i);
                        minY=std::min(minY,j);
                        maxX=std::max(maxX,i);
                        maxY=std::max(maxY,j);
                    }
                }
            }
            if(maxX<minX)
                return 0;
            return (maxX-minX+1)*(maxY-minY+1);
        };


        int ans=solve(0,n-1,0,m-1);
        for(int col=0;col<m;col++)
        {
            for(int row=0;row<n;row++)
            {
                ans=std::min(ans,solve(0,row,0,col)+solve(row+1,n-1,0,col)+solve(0,n-1,col+1,m-1));
                ans=std::min(ans,solve(0,n-1,0,col)+solve(0,row,col+1,m-1)+solve(row+1,n-1,col+1,m-1));
            }
        }

        for(int row=0;row<n;row++)
        {

            for(int col=0;col<m;col++)
            {
                ans=std::min(ans,solve(0,row,0,m-1)+solve(row+1,n-1,0,col)+solve(row+1,n-1,col+1,m-1));
                ans=std::min(ans,solve(0,row,0,col)+solve(0,row,col+1,m-1)+solve(row+1,n-1,0,m-1));
            }
        }

        for(int row1=0;row1<n;row1++)
            for(int row2=row1+1;row2<n;row2++)
                ans=std::min(ans,solve(0,row1,0,m-1)+solve(row1+1,row2,0,m-1)+solve(row2+1,n-1,0,m-1));

        for(int col1=0;col1<m;col1++)
            for(int col2=col1+1;col2<m;col2++)
                ans=std::min(ans,solve(0,n-1,0,col1)+solve(0,n-1,col1+1,col2)+solve(0,n-1,col2+1,m-1));
        return ans;
    }
};
