class Solution:
    def minimumSum(self, grid: List[List[int]]) -> int:
        n, m = len(grid), len(grid[0])

        def minimumArea(x0, x1, y0, y1):
            min_x, min_y = x1, y1
            max_x, max_y = x0, y0
            count = 0
            for i in range(x0, x1 + 1):
                for j in range(y0, y1 + 1):
                    if grid[i][j] == 1:
                        count += 1
                        min_x = min(min_x, i)
                        min_y = min(min_y, j)
                        max_x = max(max_x, i)
                        max_y = max(max_y, j)
            # print(x0,x1,y0,y1, (max_x - min_x + 1) * (max_y - min_y + 1))
            if count == 0:
                return 0

            return (max_x - min_x + 1) * (max_y - min_y + 1)

        res = n * m
        for xline in range(n-1):
            for yline in range(m-1):
                res = min(minimumArea(0,xline,0,m-1) + minimumArea(xline+1,n-1,0,yline) + minimumArea(xline+1,n-1,yline+1,m-1), res)
                res = min(minimumArea(xline+1,n-1,0,m-1) + minimumArea(0,xline,0,yline) + minimumArea(0,xline,yline+1,m-1), res)

                res = min(minimumArea(0,xline,0,yline) + minimumArea(xline+1,n-1,0,yline) + minimumArea(0,n-1,yline+1,m-1), res)
                res = min(minimumArea(0,n-1,0,yline) + minimumArea(0,xline,yline+1,m-1) + minimumArea(xline+1,n-1,yline+1,m-1), res)
        # print(n, m)
        for xline1 in range(n - 2):
            for xline2 in range(xline1 + 1, n - 1):
                res = min(minimumArea(0,xline1,0,m-1) + minimumArea(xline1+1,xline2,0,m-1) + minimumArea(xline2+1,n-1,0,m-1), res)

        # print(minimumArea(0,1,0,m-1) , minimumArea(1+1,2,0,m-1) , minimumArea(3,4,0,m-1))

        for xline1 in range(m - 2):
            for xline2 in range(xline1 + 1, m - 1):
                res = min(minimumArea(0,n-1,0,xline1) + minimumArea(0,n-1,xline1+1,xline2) + minimumArea(0,n-1,xline2+1,m-1), res)

        return res
