class Solution {
    public int minimumSum(int[][] grid) {
        int m=grid.length, n=grid[0].length, ans=1000;

        for(int i=1;i<m;i++){
            ans=Math.min(ans,rec1(grid,0,i-1,0,n-1)+rec2(grid,i,m-1,0,n-1));
            ans=Math.min(ans,rec2(grid,0,i-1,0,n-1)+rec1(grid,i,m-1,0,n-1));
        }

        for(int j=1;j<n;j++){
            ans=Math.min(ans,rec1(grid,0,m-1,0,j-1)+rec2(grid,0,m-1,j,n-1));
            ans=Math.min(ans,rec2(grid,0,m-1,0,j-1)+rec1(grid,0,m-1,j,n-1));
        }

        // System.out.println(rec1(grid,0,1,0,2));

        return ans;
    }

    private int rec1(int[][] grid, int m1, int m2, int n1, int n2){
        int x1=m1, x2=m2, y1=n1,y2=n2, sum=0;
        for(int i=m1;i<=m2;i++){
            for(int j=n1;j<=n2;j++){
                sum+=grid[i][j];
            }
        }
        if(sum==0) return 1000;

        sum=0;
        while(sum==0){
            for(int j=n1;j<=n2;j++) sum+=grid[x1][j];
            x1++;
        }
        x1--;

        sum=0;
        while(sum==0){
            for(int j=n1;j<=n2;j++) sum+=grid[x2][j];
            x2--;
        }
        x2++;

        sum=0;
        while(sum==0){
            for(int i=m1;i<=m2;i++) sum+=grid[i][y1];
            y1++;
        }
        y1--;

        sum=0;
        while(sum==0){
            for(int i=m1;i<=m2;i++) sum+=grid[i][y2];
            y2--;
        }
        y2++;

        return (x2-x1+1)*(y2-y1+1);
    }

    private int rec2(int[][] grid, int m1, int m2, int n1, int n2){
        int ans=1000;
        for(int i=m1+1;i<=m2;i++){
            int s1=rec1(grid,m1,i-1,n1,n2), s2=rec1(grid,i,m2,n1,n2);
            ans=Math.min(ans,s1+s2);
        }

        for(int j=n1+1;j<=n2;j++){
            int s1=rec1(grid,m1,m2,n1,j-1), s2=rec1(grid,m1,m2,j,n2);
            ans=Math.min(ans,s1+s2);
        }

        return ans;
    }
}
