class Solution {
public:
    int n, m;
    int get(vector<vector<int>>& grid, int i1, int i2, int j1, int j2){
        int minv = 0x3f3f3f3f;
        bool flag = false;
        for(int i = i1; i <= i2; i ++ ){
            for(int j = j1; j <= j2; j ++ ){
                if(grid[i][j])  flag = true; 
            }
        }
        if(!flag)   return 0;

        int l, r, u, d;
        u = 0x3f3f3f3f;
        d = -1;
        l = 0x3f3f3f3f;
        r = -1;
        for(int i = i1; i <= i2; i ++ ){
            for(int j = j1; j <= j2; j ++ ){
                if(grid[i][j])  u = min(u, i), d = max(d, i);
                if(grid[i][j])  l = min(l, j), r = max(r, j); 
            }
        }
        return (d - u + 1) * (r - l + 1);
    }
    int minimumSum(vector<vector<int>>& grid) {
        n = grid.size(), m = grid[0].size();
        int res = 0x3f3f3f3f;
        if(n >= 3){
            for(int i1 = 0; i1 < n; i1 ++ ){
                for(int i2 = i1 + 1; i2 < n; i2 ++ ){
                    if(i2 == n - 1) continue;
                    int a1 = get(grid, 0, i1, 0, m - 1);
                    int a2 = get(grid, i1 + 1, i2, 0, m - 1);
                    int a3 = get(grid, i2 + 1, n - 1, 0, m - 1);
                    if(a1 == 0 || a2 == 0 || a3 == 0)   continue;
                    res = min(res, a1 + a2 + a3);
                }
            }
        }
        if(n >= 2){
            for(int i1 = 0; i1 < n; i1 ++ ){
                if(i1 == n - 1) continue;
                int a1 = get(grid, 0, i1, 0, m - 1);
                if(a1 == 0) continue;
                for(int j1 = 0; j1 < m; j1 ++ ){
                    if(j1 == m - 1) continue;
                    int a2 = get(grid, i1 + 1, n - 1, 0, j1);
                    int a3 = get(grid, i1 + 1, n - 1, j1 + 1, m - 1);
                    if(a2 == 0 || a3 == 0)  continue;
                    res = min(res, a1 + a2 + a3);
                }
            }


            for(int i1 = 0; i1 < n; i1 ++ ){
                if(i1 == n - 1) continue;
                int a1 = get(grid, i1 + 1, n - 1, 0, m - 1);
                if(a1 == 0) continue;
                for(int j1 = 0; j1 < m; j1 ++ ){
                    if(j1 == m - 1) continue;
                    int a2 = get(grid, 0, i1, 0, j1);
                    int a3 = get(grid, 0, i1, j1 + 1, m - 1);
                    if(a2 == 0 || a3 == 0)  continue;
                    res = min(res, a1 + a2 + a3);
                }
            }
        }




        if(m >= 3){
            for(int j1 = 0; j1 < m; j1 ++ ){
                for(int j2 = j1 + 1; j2 < m; j2 ++ ){
                    if(j2 == m - 1) continue;
                    int a1 = get(grid, 0, n - 1, 0, j1);
                    int a2 = get(grid, 0, n - 1, j1 + 1, j2);
                    int a3 = get(grid, 0, n - 1, j2 + 1, m - 1);
                    if(a1 == 0 || a2 == 0 || a3 == 0)   continue;
                    res = min(res, a1 + a2 + a3);
                }
            }
        }

        if(m >= 2){
            for(int j1 = 0; j1 < m; j1 ++ ){
                if(j1 == m - 1) continue;
                int a1 = get(grid, 0, n - 1, 0, j1);
                for(int i1 = 0; i1 < n; i1 ++ ){
                    if(i1 == n - 1) continue;
                    int a2 = get(grid, 0, i1, j1 + 1, m - 1);
                    int a3 = get(grid, i1 + 1, n - 1, j1 + 1, m - 1);
                    if(a1 == 0 || a2 == 0 || a3 == 0)   continue;
                    res = min(res, a1 + a2 + a3);
                }
            }


            for(int j1 = 0; j1 < m; j1 ++ ){
                if(j1 == m - 1) continue;
                int a1 = get(grid, 0, n - 1, j1 + 1, m - 1);
                for(int i1 = 0; i1 < n; i1 ++ ){
                    if(i1 == n - 1) continue;
                    int a2 = get(grid, 0, i1, 0, j1);
                    int a3 = get(grid, i1 + 1, n - 1, 0, j1);
                    if(a1 == 0 || a2 == 0 || a3 == 0)   continue;
                    res = min(res, a1 + a2 + a3);
                }
            }
        }



        return res;
    }
