class Solution {
public:
    int minimumArea(int si, int sj, int ei, int ej, vector<vector<int>>& grid) {
        //cout << si << "," <<sj << " : " << ei <<"," <<ej << " = ";
        int t = ei, b = si, l = ej, r = sj;
        for(int i=si; i<=ei; i++) {
            for(int j=sj; j<=ej; j++) {
                if(grid[i][j] == 1) {
                    t = min(t, i);
                    b = max(b, i);
                    l = min(l, j);
                    r = max(r, j);
                }
            }
        }
        //cout << (b-t+1)*(r-l+1) << endl;
        return max(0, (b-t+1)*(r-l+1));
    }


    int minimumSum(vector<vector<int>>& grid) {
        int row = grid.size();
        int col = grid[0].size();
        int ans = 900, seg1, seg2, seg3, seg4;
        for(int i=0; i<row-1; i++) {
            for(int j=0; j<col-1; j++) {
                seg1 = minimumArea(0,0, i,j, grid)     + minimumArea(i+1,0, row-1,j, grid)    + minimumArea(0,j+1,row-1,col-1,grid);
                seg2 = minimumArea(0,0, i,j, grid)     + minimumArea(i+1,0, row-1,col-1,grid) + minimumArea(0,j+1, i,col-1, grid);
                seg3 = minimumArea(0,0, row-1,j, grid) + minimumArea(0,j+1, i,col-1,grid)     + minimumArea(i+1,j+1, row-1,col-1, grid);
                seg4 = minimumArea(0,0, i,col-1, grid) + minimumArea(i+1,0, row-1,j, grid)    + minimumArea(i+1,j+1, row-1, col-1, grid);

                ans = min({ans, seg1, seg2, seg3, seg4});
                //cout << "===================" <<i << j << ans << endl;
            }
        }

        for(int i=0; i<row-2; i++) {
            for(int j=i+1; j<row-1; j++) {
                seg1 = minimumArea(0,0, i,col-1, grid) + minimumArea(i+1,0,j,col-1, grid) + minimumArea(j+1,0, row-1,col-1,grid);
                ans = min(ans, seg1);
                //cout << "===================" <<i << j << ans << endl;
            }
        }

        for(int i=0; i<col-2; i++) {
            for(int j=i+1; j<col-1; j++) {
                seg1 = minimumArea(0,0, row-1,i, grid) + minimumArea(0,i+1,row-1,j, grid) + minimumArea(0,j+1, row-1,col-1,grid);
                ans = min(ans, seg1);
            }
        }

        return ans;
    }
};
