class Solution {
public:
    int cal(vector<vector<int>>& grid, int x1, int x2, int y1, int y2) {
        if(x1 > x2 || y1 > y2) return 0;
        int left = y2;
        int right = y1;
        int up = x2;
        int down = x1;
        bool flg = false;
        for(int i = x1; i <= x2; i++) {
            for(int j = y1; j <= y2; j++) {
                if(grid[i][j] == 1) {
                    left = min(left, j);
                    right = max(right, j);
                    up = min(i, up);
                    down = max(down, i);
                    flg = true;
                }
            }
        }
        if(!flg) return 0;
        // cout << left << " " << right << " " << up << " " << down << endl;
        // cout << x1 << " " << x2 << " " << y1 <<" " << y2 <<": "<<(right - left + 1) * (down - up + 1) << endl;
        return (right - left + 1) * (down - up + 1);
    }
    int minimumSum(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();
        int mini = m * n;
        for(int i = 0; i < m; i++) {
            for(int j = 0; j < n; j++) {
                int tmp = 0;
                tmp += cal(grid, 0, i, 0, n-1);
                tmp += cal(grid, i+1, m-1, 0, j);
                tmp += cal(grid, i+1, m-1, j+1, n-1);
                mini = min(mini, tmp);
                // cout << i << " " << j << endl;
                // cout << tmp << endl;
            }
            for(int k = i+1; k < m; k++) {
                int tmp = 0;
                tmp += cal(grid, 0, i, 0, n-1);
                tmp += cal(grid, i+1, k, 0, n-1);
                tmp += cal(grid, k+1, m-1, 0, n-1);
                mini = min(mini, tmp);
                // cout << i << " = " << k << endl;
                // cout << tmp << endl;
            }
        }
        for(int i = 0; i < n; i++) {
            for(int j = 0; j < m; j++) {
                int tmp = 0;
                tmp += cal(grid, 0, m-1, 0, i);
                tmp += cal(grid, 0, j, i+1, n-1);
                tmp += cal(grid, j+1, m-1, i+1, n-1);
                mini = min(mini, tmp);
                // cout << i << ", " << j << endl;
                // cout << tmp << endl;
            }
            for(int k = i+1; k < n; k++) {
                int tmp = 0;
                tmp += cal(grid, 0, m-1, 0, i);
                tmp += cal(grid, 0, m-1, i+1, k);
                tmp += cal(grid, 0, m-1, k+1, n-1);
                mini = min(mini, tmp);
                // cout << i << " || " << k << endl;
                // cout << tmp << endl;
            }
        }

         for(int i = 0; i < m; i++) {
            for(int j = 0; j < n; j++) {
                int tmp = 0;
                tmp += cal(grid, i+1, m-1, 0, n-1);
                tmp += cal(grid, 0, i, 0, j);
                tmp += cal(grid, 0, i, j+1, n-1);
                mini = min(mini, tmp);
                // cout << i << " " << j << endl;
                // cout << tmp << endl;
            }
        }
        for(int i = 0; i < n; i++) {
            for(int j = 0; j < m; j++) {
                int tmp = 0;
                tmp += cal(grid, 0, m-1, i+1, n-1);
                tmp += cal(grid, 0, j, 0, i);
                tmp += cal(grid, j+1, m-1, 0, i);
                mini = min(mini, tmp);
                // cout << i << ", " << j << endl;
                // cout << tmp << endl;
            }
        }
        return mini;
    }
};
