class Solution:
    def minimumSum(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        def find_min_area(left_boundary: int, right_boundary: int, top_boundary: int, bottom_boundary: int) -> int:
            left, right, top, bottom = right_boundary, left_boundary, bottom_boundary, top_boundary
            exist1 = False
            for y in range(top_boundary, bottom_boundary + 1):
                for x in range(left_boundary, right_boundary + 1):
                    if grid[y][x] == 1:
                        if not exist1:
                            exist1 = True
                        left, right, top, bottom = min(left, x), max(right, x), min(top, y), max(bottom, y)
            return (right - left + 1) * (bottom - top + 1) if exist1 else m * n
        min_area = m * n
        for i in range(1, n - 1):
            for j in range(i, n):
                min_area = min(min_area, 
                               find_min_area(0, i - 1, 0, m - 1) 
                               + find_min_area(i, j - 1, 0, m - 1) 
                               + find_min_area(j, n - 1, 0, m - 1))
        for i in range(1, m - 1):
            for j in range(i, m):
                min_area = min(min_area, 
                               find_min_area(0, n - 1, 0, i - 1) 
                               + find_min_area(0, n - 1, i, j - 1) 
                               + find_min_area(0, n - 1, j, m - 1))
        for i in range(1, n):
            for j in range(1, m):
                min_area = min(min_area, 
                               find_min_area(0, i - 1, 0, m - 1) 
                               + find_min_area(i, n - 1, 0, j - 1) 
                               + find_min_area(i, n - 1, j, m - 1))
                min_area = min(min_area, 
                               find_min_area(0, i - 1, 0, j - 1) 
                               + find_min_area(0, i - 1, j, m - 1) 
                               + find_min_area(i, n - 1, 0, m - 1))
        for i in range(1, m):
            for j in range(1, n):
                min_area = min(min_area, 
                               find_min_area(0, n - 1, 0, i - 1) 
                               + find_min_area(0, j - 1, i, m - 1) 
                               + find_min_area(j, n - 1, i, m - 1))
                min_area = min(min_area, 
                               find_min_area(0, j - 1, 0, i - 1) 
                               + find_min_area(j, n - 1, 0, i - 1) 
                               + find_min_area(0, n - 1, i, m - 1))
        return min_area
