func minimumSum(grid [][]int) (ans int) {
	ans = cut1(grid, 0, 0, len(grid)-1, len(grid[0])-1)
	// case1 最后一刀竖着切
	for i := 1; i < len(grid[0]); i++ {
		ans = min(ans, cut2(grid, 0, 0, len(grid)-1, i-1)+cut1(grid, 0, i, len(grid)-1, len(grid[0])-1))
	}
	// case2 最后一刀横着切
	for i := 1; i < len(grid); i++ {
		ans = min(ans, cut2(grid, 0, 0, i-1, len(grid[0])-1)+cut1(grid, i, 0, len(grid)-1, len(grid[0])-1))
	}
	// case3 最后一刀切的一个角
	for i := 1; i < len(grid); i++ {
		for j := 1; j < len(grid[0]); j++ {
			// 最后一个角是（i,j)左上点
			ret := cut1(grid, i, j, len(grid)-1, len(grid[0])-1)
			// 剩下的一个直角形状，切两部分，横或竖
			ans = min(ans, ret+cut1(grid, 0, 0, len(grid)-1, j-1)+cut1(grid, 0, j, i-1, len(grid[0])-1))
			ans = min(ans, ret+cut1(grid, 0, 0, i-1, len(grid[0])-1)+cut1(grid, i, 0, len(grid)-1, j-1))
		}
	}
	return
}

// 在 sx,sy -> ex,ey 这块区域，只能覆盖1个矩形，得到面积
func cut1(grid [][]int, sx, sy, ex, ey int) int {
	up, bottom, left, right := -1, -1, -1, -1
	for i := sx; i <= ex; i++ {
		for j := sy; j <= ey; j++ {
			if grid[i][j] == 1 {
				if up == -1 {
					up = i
				} else {
					up = min(up, i)
				}
				if left == -1 {
					left = j
				} else {
					left = min(left, j)
				}
				bottom = max(bottom, i)
				right = max(right, j)
			}
		}
	}

	return (bottom - up + 1) * (right - left + 1)
}

// 在 sx,sy -> ex,ey 这块矩形区域，切成2个矩形，得到面积
func cut2(grid [][]int, sx, sy, ex, ey int) int {
	ret := (ex - sx + 1) * (ey - sy + 1)
	for i := sx + 1; i <= ex; i++ {
		ret = min(ret, cut1(grid, sx, sy, i-1, ey)+cut1(grid, i, sy, ex, ey))
	}
	for i := sy + 1; i <= ey; i++ {
		ret = min(ret, cut1(grid, sx, sy, ex, i-1)+cut1(grid, sx, i, ex, ey))
	}
	return ret
}
