class Solution {
public:
    int f(vector<vector<int>>& g, int mxx, int mnx, int mxy, int mny, int k) {
        int n = g.size(), m = g[0].size();
        int xx = -1, nx = n, xy = -1, ny = m;
        for (int i = mnx; i <= mxx; i++) {
            for (int j = mny; j <= mxy; j++) {
                if (g[i][j]) {
                    xx = max(xx, i);
                    nx = min(nx, i);
                    xy = max(xy, j);
                    ny = min(ny, j);
                }
            }
        }
        if (xx == -1) {
            return 0;
        }
        if (k == 1) {
            return (xx - nx + 1) * (xy - ny + 1);
        }
        if (k == 2) {
            int a = 1000;
            for (int i = nx; i < xx; i++) {
                a = min(a, f(g, i, nx, xy, ny, 1) + f(g, xx, i + 1, xy, ny, 1));
            }
            for (int j = ny; j < xy; j++) {
                a = min(a, f(g, xx, nx, j, ny, 1) + f(g, xx, nx, xy, j + 1, 1));
            }
            return a;
        }
        int ans = 1000;
        for (int i = nx; i < xx; i++) {
            ans = min(ans, min(f(g, i, nx, xy, ny, 2) + f(g, xx, i + 1, xy, ny, 1), f(g, i, nx, xy, ny, 1) + f(g, xx, i + 1, xy, ny, 2)));
        }
        for (int j = ny; j < xy; j++) {
            ans = min(ans, min(f(g, xx, nx, j, ny, 2) + f(g, xx, nx, xy, j + 1, 1), f(g, xx, nx, j, ny, 1) + f(g, xx, nx, xy, j + 1, 2)));
        }
        return ans;
    }

    int minimumSum(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        return f(grid, n - 1, 0, m - 1, 0, 3);
    }
};
