class Solution:
    def minimumSum(self, grid: List[List[int]]) -> int:
        m = len(grid)
        n = len(grid[0])

        def check(a:int,b:int,c:int,d:int) -> int:
            l = inf
            r = -inf
            u = inf
            dd = -inf
            for i_ in range(a, b):
                for j_ in range(c, d):
                    x = grid[i_][j_]
                    if x == 1:
                        l = min(l, j_)
                        r = max(r, j_)
                        u = min(u, i_)
                        dd = max(dd, i_)
            if l == inf:
                return 0
            return (r-l+1) *(dd - u+1) 
            # print(c)
            # print(d)
            # return 1

        ans = inf
        for i in range(1,m):
            cur = check(0,i,0,n)
            for j in range(1,n):
                cur1 = check(i, m,0, j) + check(i,m,j,n)
                # print(cur1)
                mj = cur + cur1
                if mj < ans:
                    ans = mj
            for j in range(i+1,m):
                cur1 = check(i, j,0, n) + check(j,m,0,n)
                # print(cur1)
                mj = cur + cur1
                if mj < ans:
                    ans = mj

        # print(ans)       
        for i in range(1,m):
            cur = check(i,m,0,n)
            for j in range(1,n):
                cur1 = check(0, i,0, j) + check(0,i,j,n)
                mj = cur + cur1
                if mj < ans:
                    ans = mj

        for i in range(1,n):
            cur = check(0,m,0,i)
            for j in range(1,m):
                cur1 = check(0, j,i, n) + check(j,m,i,n)
                mj = cur + cur1
                if mj < ans:
                    ans = mj
            for j in range(i+1,n):
                cur1 = check(0, m,i, j) + check(0,m,j,n)
                # print(cur1)
                mj = cur + cur1
                if mj < ans:
                    ans = mj

        for i in range(1,n):
            cur = check(0,m,i,n)
            for j in range(1,m):
                cur1 = check(0, j,0, i) + check(j,m,0,i)
                mj = cur + cur1
                if mj < ans:
                    ans = mj
        return ans
