class Solution {
    public int minimumSum(int[][] grid) {
        int ans=Integer.MAX_VALUE;
        for (int i = 0; i < grid.length ; i++) {
            ans=Integer.min(ans,one(grid,0,grid[0].length-1,0,i)+two(grid,0,grid[0].length-1,i+1, grid.length-1));
            ans=Integer.min(ans,two(grid,0,grid[0].length-1,0,i)+one(grid,0,grid[0].length-1,i+1, grid.length-1));
        }
        for (int i = 0; i < grid[0].length; i++) {
           ans=Integer.min(ans,one(grid,0,i,0,grid.length-1)+two(grid,i+1,grid[0].length-1,0,grid.length-1)) ;
            ans=Integer.min(ans,two(grid,0,i,0,grid.length-1)+one(grid,i+1,grid[0].length-1,0,grid.length-1)) ;
        }
        return ans;
    }
    public int one(int[][] grid,int xs,int xe,int ys,int ye){
        if(xs>xe||ys>ye){
            return 0;
        }
        int xMax=-1;
        int xMin=Integer.MAX_VALUE;
        int yMax=-1;
        int yMin=Integer.MAX_VALUE;
        for (int i = ys; i <=ye; i++) {
            for (int j = xs; j <= xe; j++) {
                if(grid[i][j]==1){
                    xMax=Integer.max(xMax,j);
                    xMin=Integer.min(xMin,j);
                    yMax=Integer.max(yMax,i);
                    yMin=Integer.min(yMin,i);
                }
            }
        }
        if(xMax==-1){
            return 0;
        }
        return (xMax-xMin+1)*(yMax-yMin+1);
    }
    public int two(int[][] grid,int xs,int xe,int ys,int ye){
        if(xs>xe||ys>ye){
            return 0;
        }
        int ans=Integer.MAX_VALUE;
        for (int i = ys; i <=ye ; i++) {
            ans=Integer.min(ans,one(grid,xs,xe,ys,i)+one(grid,xs,xe,i+1,ye));
        }
        for (int i = xs; i <=xe ; i++) {
            ans=Integer.min(ans,one(grid,xs,i,ys,ye)+one(grid,i+1,xe,ys,ye));
        }
        return ans;
    }
}
