class Solution:
    def minimumSum(self, grid: List[List[int]]) -> int:
        def rect1(minx, maxx, miny, maxy):
            mxx, mnx = minx, maxx
            mny, mxy = maxy, miny
            c = 0
            for y in range(miny, maxy + 1):
                for x in range(minx, maxx + 1):
                    if grid[y][x] > 0:
                        c += 1
                        mnx = min(mnx, x)
                        mxx = max(mxx, x) 
                        mny = min(mny, y)
                        mxy = max(mxy, y)
            if c == 0:
                return float('inf')
            return (mxx - mnx + 1) * (mxy - mny + 1)
        def rect2(minx, maxx, miny, maxy):
            ans = float('inf')
            for x in range(minx + 1, maxx + 1):
                ans = min(ans, rect1(minx, x - 1, miny, maxy) + rect1(x, maxx, miny, maxy))
            for y in range(miny + 1, maxy + 1):
                ans = min(ans, rect1(minx, maxx, miny, y - 1) + rect1(minx, maxx, y, maxy))
            return ans
        def rect3(minx, maxx, miny, maxy):
            ans = float('inf')
            for x in range(minx + 1, maxx + 1):
                ans = min(ans, rect1(minx, x - 1, miny, maxy) + rect2(x, maxx, miny, maxy))
                ans = min(ans, rect2(minx, x - 1, miny, maxy) + rect1(x, maxx, miny, maxy))
            for y in range(miny + 1, maxy + 1):
                ans = min(ans, rect1(minx, maxx, miny, y - 1) + rect2(minx, maxx, y, maxy))
                ans = min(ans, rect2(minx, maxx, miny, y - 1) + rect1(minx, maxx, y, maxy))
            return ans
        h, w = len(grid), len(grid[0])
        return rect3(0, w - 1, 0, h - 1)
