class Solution:
    def minimumSum(self, grid: List[List[int]]) -> int:
        m,n = len(grid),len(grid[0])
        # 找到最大的矩形
        l,r,u,d = +inf,-inf,+inf,-inf
        for i in range(m):
            for j in range(n):
                if grid[i][j]==0:continue
                l,r,u,d = min(l,j),max(r,j),min(u,i),max(d,i)
        ans = inf
        @cache
        def getV(x1,y1,x2,y2):
            l,r,u,d = +inf,-inf,+inf,-inf
            for i in range(x1,x2+1):
                for j in range(y1,y2+1):
                    if grid[i][j] == 0: continue
                    l, r, u, d = min(l, j), max(r, j), min(u, i), max(d, i)
            return (r-l+1)*(d-u+1)
        # 切割
        # 1. 水平切割，竖直留空
        for i in range(u,d):
            for j in range(l,r):
                ans = min(ans,getV(u,l,i,j)+getV(u,j+1,i,r)+getV(i+1,l,d,r))
                ans = min(ans,getV(u,l,i,r)+getV(i+1,l,d,j)+getV(i+1,j+1,d,r))
            for i2 in range(i+1,d):
                ans = min(ans,getV(u,l,i,r)+getV(i+1,l,i2,r)+getV(i2+1,l,d,r))

        # 2. 竖直切割，水平留空
        for j in range(l,r):
            for i in range(u,d):
                ans = min(ans,getV(u,l,i,j)+getV(i+1,l,d,j)+getV(u,j+1,d,r))
                ans = min(ans,getV(u,l,d,j)+getV(u,j+1,i,r)+getV(i+1,j+1,d,r))
            for j2 in range(j+1,r):
                ans = min(ans,getV(u,l,d,j)+getV(u,j+1,d,j2)+getV(u,j2+1,d,r))
        return ans
