function minimumSum(g: number[][]): number {
  const H = g.length;
  const W = g[0].length;
  const m = new Map();
  let r = H * W;

  const f = (x: number, y: number, h: number, w: number) => {
    const k = `${x},${y},${h},${w}`;

    if (m.has(k)) {
      return m.get(k);
    }

    let ha = H;
    let hb = 0;
    let wa = W;
    let wb = 0;

    // console.log(x, y, h, w);

    for (let hh = y; hh < y + h; ++hh) {
      for (let ww = x; ww < x + w; ++ww) {
        if (g[hh][ww]) {
          ha = Math.min(ha, hh);
          hb = Math.max(hb, hh);
          wa = Math.min(wa, ww);
          wb = Math.max(wb, ww);
        }
      }
    }

    let rr = (hb - ha + 1) * (wb - wa + 1);

    m.set(k, rr);
    return rr;
  };

  const f1 = (x: number, y: number, h: number, w: number) => {
    let rr = H * W;

    for (let hh = y + 1; hh < y + h; ++hh) {
      rr = Math.min(rr, f(x, y, hh - y, w) + f(x, hh, h - hh + y, w));
    }

    for (let ww = x + 1; ww < x + w; ++ww) {
      rr = Math.min(rr, f(x, y, h, ww - x) + f(ww, y, h, w - ww + x));
    }

    return rr;
  };

  for (let h = 1; h < H; ++h) {
    // console.log(0, 0, h, W, 0, h, H - h, W);
    r = Math.min(r, f(0, 0, h, W) + f1(0, h, H - h, W));
    r = Math.min(r, f1(0, 0, h, W) + f(0, h, H - h, W));
  }

  for (let w = 1; w < W; ++w) {
    // console.log(0, 0, H, w, w, 0, H, W - w);
    r = Math.min(r, f(0, 0, H, w) + f1(w, 0, H, W - w));
    r = Math.min(r, f1(0, 0, H, w) + f(w, 0, H, W - w));
  }

  return r;
}
