class Solution {
public:
    int sparsh(int ro1, int co1, int ro2, int co2, vector<vector<int>>& nums) {
        int a = 400, b = 400, c = 0, d = 0;
        int can = 0;

        for (int i = ro1; i <= ro2; i++) {
            for (int j = co1; j <= co2; j++) {
                if (nums[i][j] == 1) {
                    c = max(c, i);
                    d = max(d, j);
                    a = min(a, i);
                    b = min(b, j);
                    can++;
                }
            }
        }

        return can == 0 ? 1 : (d - b + 1)*(c - a + 1);
    }

    int minimumSum(vector<vector<int>>& nums) {

        vector<pair<int, int>> ones;
        int n = nums.size();
        int m = nums[0].size();
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < m; ++j) {
                if (nums[i][j] == 1) {
                    ones.push_back({i, j});
                }
            }
        }

        int res= 1e9;

        for (int i = 0; i < n; ++i) {
            for (int j = i + 1; j < n; ++j) {
                int ms = sparsh(0, 0, i, m - 1, nums) + sparsh(i + 1, 0, j, m - 1, nums) + sparsh(j + 1, 0, n - 1, m - 1, nums);
                res = min(res, ms);
            }
        }

        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < m; ++j) {
                int ms = sparsh(0, 0, n - 1, j, nums) + sparsh(0, j + 1, i, m - 1, nums) + sparsh(i + 1, j + 1, n - 1, m - 1, nums);
                res = min(res, ms);

                ms = sparsh(0, 0, i, j, nums) + sparsh(i + 1, 0, n - 1, j, nums) + sparsh(0, j + 1, n - 1, m - 1, nums);
                res = min(res, ms);
            }
        }

        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < m; ++j) {
                int ms = sparsh(0, 0, i, j, nums) + sparsh(0, j + 1, i, m - 1, nums) + sparsh(i + 1, 0, n - 1, m - 1, nums);
                res = min(res, ms);

                ms = sparsh(0, 0, i, m - 1, nums) + sparsh(i + 1, j + 1, n - 1, m - 1, nums) + sparsh(i + 1, 0, n - 1, j, nums);
                res = min(res, ms);
            }
        }

        for (int i = 0; i < m; ++i) {
            for (int j = i + 1; j < m; ++j) {
                int ms = sparsh(0, 0, n - 1, i, nums) + sparsh(0, i + 1, n - 1, j, nums) + sparsh(0, j + 1, n - 1, m - 1, nums);
                res = min(res, ms);
            }
        }

        return res;
    }
};
