class Solution {
public:

    int get2(int &x1 , int &x2 , int &y1 , int &y2 , vector<vector<int>>&grid)
    {
        int mxX = -1e9 , mnX = 1e9 , mnY = 1e9 , mxY = -1e9 ; 

        for(int i = x1 ; i<=x2; ++i)
        {
            for(int j = y1 ; j<=y2; ++j)
            {
                if(grid[i][j] == 0)
                    continue ; 
                mxX = max(mxX , i) ; 
                mnX = min(mnX , i);
                mnY = min(mnY , j);
                mxY = max(mxY , j);
            }
        }

        if(mxX == -1e9)
            return 0 ; 
        int area = (mxX - mnX + 1);
        area *= (mxY - mnY + 1);
        return area ; 
    }

    int get(int x1, int x2 , int y1 , int y2 , int xx1 , int xx2 , int yy1 , int yy2 , int xxx1 , int xxx2 , int yyy1 , int yyy2 , vector<vector<int>>&grid)
    {

        int fir = get2(x1,x2,y1,y2,grid);
        int sec = get2(xx1,xx2,yy1,yy2,grid);
        int thi = get2(xxx1,xxx2,yyy1,yyy2,grid);

        fir+=sec ;
        fir+=thi;

        return fir ; 
    }



    int minimumSum(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size() ; 
        int ans = 1e9 ; 
        for(int i = 0 ; i<n-1; ++i)
        {
            for(int j = 0 ; j<m-1; ++j)
            {
                //vertical 
                int ver =get(0,i,0,j,0,n-1,j+1,m-1,i+1,n-1,0,j,grid);
                int hor = get(0,i,0,j,0,i,j+1,m-1,i+1,n-1,0,m-1,grid);
                ans = min(ans, ver) ; 
                ans = min(ans,hor);
            }
        }

        //all hor
        for(int i = 0 ; i<=n-3; ++i)
        {
            for(int j = i+1 ; j<=n-2; ++j)
            {

                    ans = min(ans ,  get( 0 , i , 0 , m -1 , i+1 ,  j , 0 , m-1 , j+1 , n-1 , 0 , m-1,grid)); 
            }
             for(int j = 0 ; j<=m-2; ++j)
            {

                    ans = min(ans ,  get( 0 , i , 0 , m -1 , i+1 ,  n-1 , 0 , j , i+1 , n-1 , j+1 , m-1,grid)); 
            }
        }

        // all ver
        for(int i = 0 ; i<=m-3; ++i)
        {
            for(int j = i+1 ; j<=m-2; ++j)
            {
                ans = min(ans ,  get( 0 , n-1 , 0 , i , 0 ,  n-1 , i+1 , j , 0 , n-1 , j+1 , m-1,grid)); 
            }
            for(int j = 0 ; j<=n-2; ++j)
            {
                ans = min(ans, get(0,n-1,0,i, 0,j,i+1,m-1 , j+1,n-1,i+1,m-1,grid));
            }
        }
        return ans ; 
    }
};
