class Solution {
      public int minimumSum(int[][] grid) {
          //||
          int ans = Integer.MAX_VALUE;
          int m = grid.length;
          int n = grid[0].length;
          for(int i=0; i < n-2; i++){
              for(int j = i+1; j < n-1; j++){
                  //两个竖着的
                  int a = get(grid,0,0,i,m-1);
                  int b = get(grid,i+1,0,j,m-1);
                  int c = get(grid,j+1,0,n-1,m-1);
                  if(a==0||b==0||c==0) continue;
                  ans = Math.min(a+b+c,ans);
              }
          }
          //=
          for(int i = 0; i < m-2; i++){
              for(int j = i+1; j < m-1; j++){
                  int a = get(grid,0,0,n-1,i);
                  int b = get(grid,0,i+1,n-1,j);
                  int c = get(grid,0,j+1,n-1,m-1);
                  if(a==0||b==0||c==0) continue;

                  ans = Math.min(a+b+c,ans);
              }
          }
          //-|
          //|-
          for(int i = 0; i < m-1; i++){
              for(int j = 0; j < n-1; j++){
                  int a = get(grid,0,0,j,i);
                  int b = get(grid,0,i+1,j,m-1);
                  int c = get(grid,j+1,0,n-1,m-1);
                  if(a!=0&&b!=0&&c!=0){
                      ans = Math.min(a+b+c,ans);
                  }

                  a = get(grid,0,0,j,m-1);
                  b = get(grid,j+1,0,n-1,i);
                  c = get(grid,j+1,i+1,n-1,m-1);
                  if(a!=0&&b!=0&&c!=0){
                      ans = Math.min(a+b+c,ans);
                  }

                  a = get(grid,0,0,n-1,i);
                  b = get(grid,0,i+1,j,m-1);
                  c = get(grid,j+1,i+1,n-1,m-1);
                  if(a!=0&&b!=0&&c!=0){
                      ans = Math.min(a+b+c,ans);
                  }

                  a = get(grid,0,0,j,i);
                  b = get(grid,j+1,0,n-1,i);
                  c = get(grid,0,i+1,n-1,m-1);
                  if(a!=0&&b!=0&&c!=0){
                      ans = Math.min(a+b+c,ans);
                  }
              }
          }

          return ans;
      }

      private int get(int[][] grid, int left, int top, int right, int down){
          int l = right;
          int r = left;
          int u = down;
          int d = top;
          for(int i = top; i <= down; i++){
              for(int j = left; j <= right; j++){
                  if(grid[i][j]==1){
                      l = Math.min(j,l);
                      r = Math.max(j,r);
                      u = Math.min(i,u);
                      d = Math.max(i,d);
                  }
              }
          }

          if(l>r||u>d) return 0;
          int w = r-l+1;
          int h = d-u+1;
          return w*h;
      }
  }
