class Solution:
    def minimumSum(self, grid: List[List[int]]) -> int:

        def get_area(x1, x2, y1, y2):
            xmin, xmax, ymin, ymax = -1, -1, -1, -1
            for i in range(x1, x2):
                for j in range(y1, y2):
                    if grid[i][j] == 1:
                        if xmin == -1:
                            xmin = i

                        xmax = i

                        if ymin == -1:
                            ymin = j
                        else:
                            ymin = min(ymin, j)

                        if ymax == -1:
                            ymax = j
                        else:
                            ymax = max(ymax, j)
            return (xmax-xmin + 1) * (ymax - ymin + 1)

        ans = float('inf')

        for _ in range(2):
            for a in range(1, len(grid) - 1):
                for b in range(a + 1, len(grid)):
                    area = (get_area(0, a, 0, len(grid[0]))
                            + get_area(a, b, 0, len(grid[0]))
                            + get_area(b, len(grid), 0, len(grid[0]))
                           )
                    if area < ans: ans = area        


            for a in range(1, len(grid)):
                for b in range(1, len(grid[0])):
                    area = (get_area(0, a, 0, len(grid[0]))
                            + get_area(a, len(grid), b, len(grid[0]))
                            + get_area(a, len(grid), 0, b)
                           )
                    if area < ans: ans = area        

            for a in range(1, len(grid)):
                for b in range(1, len(grid[0])):
                    area = (get_area(a, len(grid), 0, len(grid[0]))
                            + get_area(0, a, b, len(grid[0]))
                            + get_area(0, a, 0, b)
                           )
                    if area < ans: ans = area        

            grid = list(zip(*grid))

        return ans
