class Solution {
    vector<vector<int>> grid;
    int n, m;
public:
    int minimumSum(vector<vector<int>>& A) {
        grid = A;
        n = grid.size();
        m = grid[0].size();

        int ans = 1e9;

        for (int k=0; k<m; k++) {
            for (int i=0; i<k; i++) {
                int a = rect(0, n-1, 0, i);
                int b = rect(0, n-1, i+1, k);
                int c = rect(0, n-1, k+1, m-1);

                if (a!=0 && b!=0 && c!=0) ans = min(ans, a+b+c);
            }
            for (int i=k+1; i<m; i++) {
                int a = rect(0, n-1, k+1, i);
                int b = rect(0, n-1, i+1, m-1);
                int c = rect(0, n-1, 0, k);

                if (a!=0 && b!=0 && c!=0) ans = min(ans, a+b+c);
            }
            for (int j=0; j<n; j++) {
                int a = rect(0, j, 0, k);
                int b = rect(j+1, n-1, 0, k);
                int c = rect(0, n-1, k+1, m-1);
                if (a!=0 && b!=0 && c!=0) ans = min(ans, a+b+c);

                a = rect(0, j, k+1, m-1);
                b = rect(j+1, n-1, k+1, m-1);
                c = rect(0, n-1, 0, k);
                if (a!=0 && b!=0 && c!=0) ans = min(ans, a+b+c);
            }
        }

        for (int k=0; k<n; k++) {
            for (int i=0; i<k; i++) {
                int a = rect(0, i, 0, m-1);
                int b = rect(i+1, k, 0, m-1);
                int c = rect(k+1, n-1, 0, m-1);

                if (a!=0 && b!=0 && c!=0) ans = min(ans, a+b+c);
            }
            for (int i=k+1; i<n; i++) {
                int a = rect(k+1, i, 0, m-1);
                int b = rect(i+1, n-1, 0, m-1);
                int c = rect(0, k, 0, m-1);

                if (a!=0 && b!=0 && c!=0) ans = min(ans, a+b+c);
            }
            for (int j=0; j<m; j++) {
                int a = rect(0, k, 0, j);
                int b = rect(0, k, j+1, m-1);
                int c = rect(k+1, n-1, 0, m-1);
                if (a!=0 && b!=0 && c!=0) ans = min(ans, a+b+c);

                a = rect(k+1, n-1, 0, j);
                b = rect(k+1, n-1, j+1, m-1);
                c = rect(0, k, 0, m-1);
                if (a!=0 && b!=0 && c!=0) ans = min(ans, a+b+c);
            }
        }

        return ans;
    }

    int rect(int a, int b, int c, int d) {
        if (a > b || c > d) return 0;

        int tmx = -1, tmn = 1e9;
        int lmx = -1, lmn = 1e9;

        for (int i=a; i<=b; i++){
            for(int j=c; j<=d; j++) {
                if (!grid[i][j]) continue;
                tmn = min(tmn, i);
                tmx = max(tmx, i);

                lmn = min(lmn, j);
                lmx = max(lmx, j);
            }
        }

        if (tmx == -1) return 0;

        return (lmx-lmn+1)*(tmx-tmn+1);
    }
};
