class Solution {
public:
    const int inf = 1e9;
    int get(vector<vector<int>>& g, int d, int u, int l, int r) {
        int ans = 1e9;
        int mic = 1e9, mxc = -1, mir = 1e9, mxr = -1;
        for(int i = d; i <= u; i ++) {
            for(int j = l; j <= r; j ++) {
                if(g[i][j]) {
                    mic = min(mic, j), mxc = max(mxc, j);
                    mir = min(mir, i), mxr = max(mxr, i);
                }
            }
        }

        if(mxc == -1 || mxr == -1) return inf;
        return (mxc - mic + 1) * (mxr - mir + 1);
    }



    int minimumSum(vector<vector<int>>& grid) {

        int ans = 1e9;
        int n = grid.size(), m = grid[0].size();

        for(int i = 0; i < n - 1; i ++) {
            int p = get(grid, 0, i, 0, m - 1);
            int q = get(grid, i + 1, n - 1, 0, m - 1);
            if(i < n - 2) {
                for(int j = i + 1; j < n - 1; j ++) {
                    ans = min(ans, p + get(grid, i + 1, j, 0, m - 1) + get(grid, j + 1, n - 1, 0, m - 1));
                }
            }
            for(int j = 0; j < m - 1; j ++) {
                ans = min(ans, p + get(grid, i + 1, n - 1, 0, j) + get(grid, i + 1, n - 1, j + 1, m - 1));
                ans = min(ans, q + get(grid, 0, i, 0, j) + get(grid, 0, i, j + 1, m - 1));
            }
        }

        for(int i = 0; i < m - 1; i ++) {
            int p = get(grid, 0, n - 1, 0, i);
            int q = get(grid, 0, n - 1, i + 1, m - 1);
            if(i < m - 2) {
                for(int j = i + 1; j < m - 1; j ++) {
                    ans = min(ans, p + get(grid, 0, n - 1, i + 1, j) + get(grid, 0, n - 1, j + 1, m - 1));
                }
            }
            for(int j = 0; j < n - 1; j ++) {
                ans = min(ans, p + get(grid, 0, j, i + 1, m - 1) + get(grid, j + 1, n - 1, i + 1, m - 1));
                ans = min(ans, q + get(grid, 0, j, 0, i) + get(grid, j + 1, n - 1, 0, i));
            }
        }


        return ans;
    }
};
