class Solution:
    def minimumSum(self, grid: List[List[int]]) -> int:
        R, C = len(grid), len(grid[0])
        @lru_cache(None)
        def helper(r0, r1, c0, c1):
            # print(r0, r1, c0, c1)
            if r0 >= r1 or c0 >= c1:
                return 0, inf

            rd1 = 0
            rx1 = R - 1
            cd1 = 0 
            cx1 = C - 1 
            z1 = 0

            for r in range(r0, r1):
                for c in range(c0, c1):
                    if grid[r][c] == 1:
                        rx1 = min(rx1, r)
                        rd1 = max(rd1, r)
                        cx1 = min(cx1, c)
                        cd1 = max(cd1, c)
                        z1 += 1 
            return z1, (rd1 - rx1 + 1) * (cd1 - cx1 + 1)
        res = inf
        for r1 in range(R):
            z1, h1 = helper(0, r1 + 1, 0, C)
            z4, h4 = helper(r1 + 1, R, 0, C)
            if z1 > 0 and z4 > 1:
                for r2 in range(r1 + 1, R): 
                    z2, h2 = helper(r1 + 1, r2 + 1, 0, C) 
                    z3, h3 = helper(r2 + 1, R, 0, C)
                    if z2 > 0 and z3 > 0:
                        res = min(res, h1 + h2 + h3) 
            for c1 in range(C):
                z5, h5 = helper(0, r1 + 1, 0, c1 + 1)
                z6, h6 = helper(0, r1 + 1, c1 + 1, C)
                z7, h7 = helper(r1 + 1, R, 0, c1 + 1)
                z8, h8 = helper(r1 + 1, R, c1 + 1, C)
                if z7 > 0 and z8 > 0:
                    res = min(res, h1 + h7 + h8) 
                if z5 > 0 and z6 > 0:
                    res = min(res, h4 + h5 + h6)  
        for c1 in range(C):
            z1, h1 = helper(0, R, 0, c1 + 1)
            z4, h4 = helper(0, R, c1 + 1, C)
            if z1 > 0 and z4 > 1:
                for c2 in range(c1 + 1, C):  
                    z2, h2 = helper(0, R, c1 + 1, c2 + 1) 
                    z3, h3 = helper(0, R, c2 + 1, C)
                    if z2 > 0 and z3 > 0:
                        res = min(res, h1 + h2 + h3) 
            # print('!!')
            for r1 in range(R):
                z5, h5 = helper(0, r1 + 1, 0, c1 + 1)
                z6, h6 = helper(0, r1 + 1, c1 + 1, C)
                z7, h7 = helper(r1 + 1, R, 0, c1 + 1)
                z8, h8 = helper(r1 + 1, R, c1 + 1, C)
                if z5 > 0 and z7 > 0:
                    res = min(res, h4 + h5 + h7) 
                if z6 > 0 and z8 > 0:
                    res = min(res, h1 + h6 + h8)  
        return res
