class Solution {
public:
    int func(vector<vector<int>> &grid , int a , int b , int x , int y)
    {
        int low=x;
        int high=y;
        while(low <= high)
        {

            int cnt = 0 , cnt2 = 0;
            for(int i = a;i<b+1;i++) 
                cnt += grid[i][low];
            for(int i = a;i<b+1;i++) 
                cnt2 += grid[i][high];
            if(cnt == 0) low+=1;
            if(cnt2 == 0) high-=1;
            if(cnt && cnt2) break;
        }

        int upr = a , niche = b;
        while(upr <= niche)
        {

            int ct = 0 , ct2 = 0;
            for(int i = low;i<high+1;i++) 
                ct += grid[upr][i];
            for(int i = low;i<high+1;i++) 
                ct2 += grid[niche][i];
            if(ct == 0) upr+=1;
            if(ct2 == 0) niche-=1;
            if(ct && ct2) break;
        }
        return max(0 , high-low+1)*max(0,niche-upr+1);
    }

    int minimumSum(vector<vector<int>>& grid){
        int ans = INT_MAX;
        int n = grid.size();
        int m = grid[0].size();
        for(int i = 0;i<n-1;i++)
        {
            for(int j = 0;j<m-1;j++)
            {
                int a = func(grid , 0 , i , 0 , m-1);
                int b = func(grid , i+1 , n-1 , 0 , j);
                int c = func(grid , i+1 , n-1 , j+1 , m-1);
                ans = min(ans , a+b+c);
            }
        }
          for(int i = 0;i<n-1;i++)
        {
            for(int j = 0;j<m-1;j++)
            {
                int a = func(grid , 0 , i , 0 , j);
                int b = func(grid , 0 , i , j+1 , m-1);
                int c = func(grid , i+1 , n-1 , 0 , m-1);
                ans = min(ans , a+b+c);
            }
        }
          for(int i = 0;i<m-1;i++)
        {
            for(int j = 0;j<n-1;j++)
            {
                int a = func(grid , 0 , n-1 , 0 , i);
                int b = func(grid , 0 , j , i+1 , m-1);
                int c = func(grid , j+1 , n-1 , i+1 , m-1);
                ans = min(ans , a+b+c);
            }
        }
        for(int i = 0;i<m-1;i++)
        {
            for(int j = 0;j<n-1;j++)
            {
                int a = func(grid , 0 , j , 0 , i);
                int b = func(grid , j+1 , n-1 , 0 , i);
                int c = func(grid , 0 , n-1 , i+1 , m-1);
                ans = min(ans , a+b+c);
            }
        }
         for(int i = 0;i<n-2;i++)
        {
            for(int j = i+1;j<n-1;j++)
            {   
                int a = func(grid , 0 , i , 0 , m-1);
                int b = func(grid , i+1 , j , 0 , m-1);
                int c = func(grid , j+1 , n-1 , 0 , m-1);
                ans = min(ans , a+b+c);
            }
        }

        for(int i = 0;i<m-2;i++)
        {
            for(int j = 0;j<m-1;j++)
            {
                int a = func(grid , 0 , n-1 , 0 , i);
                int b = func(grid , 0 , n-1 , i+1 , j);
                int c = func(grid , 0 , n-1 , j+1 , m-1);
                ans = min(ans , a+b+c);
            }
        }

        return ans;
    }
};
