class Solution:
    def minimumSum(self, grid: List[List[int]]) -> int:
        row, col = len(grid), len(grid[0])
        @lru_cache(None)
        def helper(a, b, c, d):
            min_row = min_col = float("inf")
            max_row = max_col = float("-inf")
            if c < a or d < b:
              return (min_row, min_col, max_row, max_col, float("inf"), False)
            up = helper(a, b, c - 1, d)
            left = helper(a, b, c , d - 1)

            if grid[c][d] == 1:
                find = True
                min_row = min(c, up[0], left[0])
                min_col = min(d, up[1], left[1])
                max_row = max(c, up[2], left[2])
                max_col = max(d, up[3], left[3])
            else:
                find = False
                min_row = min(up[0], left[0])
                min_col = min(up[1], left[1])
                max_row = max(up[2], left[2])
                max_col = max(up[3], left[3])
            find = find or up[5] or left[5]
            res = (max_row - min_row + 1) * (max_col - min_col + 1) if find else 1
            return (min_row, min_col, max_row, max_col, res, find)

        ans = float("inf")
        for i in range(row-1):
            for j in range(col-1):
                t = helper(0,0, i, j)[4] + helper(i+1, 0 , row-1, j)[4]  + helper(0, j+1, row-1, col-1)[4]
                ans = min(ans, t)
                t = helper(0,0, i, col-1)[4] + helper(i+1, 0 , row-1, j)[4]  + helper(i+1, j+1, row-1, col-1)[4]
                ans = min(ans, t)
                t = helper(0,0, i, j)[4] + helper(0, j+1 , i, col-1)[4]  + helper(i+1, 0, row-1, col-1)[4]
                ans = min(ans, t)
        # print(helper(0,0, 1, 0)[4], helper(0,1, 0, 1)[4], helper(1,1, 1, 1)[4]) #i=1, j= 1
        for i in range(row-1):
            for j in range(1, col):
                t = helper(0, 0, row-1, j-1)[4] + helper(0,j, i, col-1)[4]  + helper(i+1,j, row-1, col-1)[4]
                ans = min(ans, t)
        for i in range(col-2):
            for j in range(i+1, col-1):
                t = helper(0, 0, row-1, i)[4] + helper(0,i+1, row-1, j)[4]  + helper(0,j+1, row-1, col-1)[4]
                # print(i, j, t, helper(0,1, row-1, 2)[4])
                ans = min(ans, t)
        for i in range(row-2):
            for j in range(i+1, row-1):
                t = helper(0, 0, i, col-1)[4] + helper(i+1, 0, j, col-1)[4]  + helper(j+1,0, row-1, col-1)[4]
                ans = min(ans, t)

        return ans
