function minimumSum(grid: number[][]): number {
    let m=grid.length;
    let n=grid[0].length;
    let d=new Map<number,number>();
    function key(x1:number,y1:number,x2:number,y2:number){
        return x1*31*31*31+y1*31*31+x2*31+y2;
    }
    function minArea(x1:number,y1:number,x2:number,y2:number){
        let k=key(x1,y1,x2,y2);
        if(k in d)return d[k];
        let minX=114514;
        let minY=114514;
        let maxX=0;
        let maxY=0;
        for(let i=x1;i<=x2;i++){
            for(let j=y1;j<=y2;j++){
                if(grid[i][j]==1){
                    minX=Math.min(minX,i);
                    maxX=Math.max(maxX,i);
                    minY=Math.min(minY,j);
                    maxY=Math.max(maxY,j);
                }
            }
        }
        d[k]=(maxX-minX+1)*(maxY-minY+1);
        return (maxX-minX+1)*(maxY-minY+1);
    }

    function div2(x1,y1,x2,y2){
        let ans=1145141919810;
        if(x2-x1>0){
            for(let i=x1;i<x2;i++){
                ans=Math.min(ans,minArea(x1,y1,i,y2)+minArea(i+1,y1,x2,y2));
            }
        }
        if(y2-y1>0){
            for(let i=y1;i<y2;i++){
                ans=Math.min(ans,minArea(x1,y1,x2,i)+minArea(x1,i+1,x2,y2));
            }
        }
        return ans;
    }

    let ans=1145141919810;
    for(let i=0;i<m-1;i++){
        ans=Math.min(ans,minArea(0,0,i,n-1)+div2(i+1,0,m-1,n-1));
        // console.log(i,minArea(0,0,i,n-1),div2(i+1,0,m-1,n-1));
    }
    for(let i=0;i<n-1;i++){
        ans=Math.min(ans,minArea(0,0,m-1,i)+div2(0,i+1,m-1,n-1));
    }
    for(let i=0;i<m-1;i++){
        for(let j=0;j<n-1;j++){
            let a=Math.min(
                minArea(i+1,0,m-1,j)+minArea(0,j+1,m-1,n-1),
                minArea(i+1,0,m-1,n-1)+minArea(0,j+1,i,n-1)
            );
            ans=Math.min(ans,minArea(0,0,i,j)+a);
        }
    }
    return ans;
};
