class Solution {
public:
    int min(int a, int b) {
        return a<b?a:b;
    }
    int max(int a, int b) {
        return a>b?a:b;
    }
    int minimumArea(vector<vector<int>>& a, int si, int ei, int sj, int ej) {
        int i,j,k;
        int n = a.size();
        int m = a[0].size();
        int mni,mnj,mxi,mxj;
        mni = n;
        mnj = m;
        mxi = mxj = 0;
        for(i=si;i<ei;++i) {
            for (j=sj;j<ej;++j) {
                if (a[i][j] == 1) {
                    mni = min(mni, i);
                    mnj = min(mnj, j);
                    mxi = max(mxi, i);
                    mxj = max(mxj, j);
                }
            }
        }
        if (mni > mxi) return 0;
        return (mxi-mni+1)*(mxj-mnj+1);
    }
    void reverse(vector<vector<int>>& a) {
        vector<vector<int>> b;
        vector<int> c;
        int i,j,k;
        int n = a.size();
        int m = a[0].size();
        for (j=0;j<m;++j) {
            b.push_back(c);
            for (i=0;i<n;++i) {
                b[j].push_back(a[n-i-1][j]);
            }
        }
        a = b;
    }
    int check(vector<vector<int>>& a) {
        int i,j,k;
        int n = a.size();
        int m = a[0].size();
        int res = n * m;
        for (i=0;i<n-1;++i) {
            int c1 = minimumArea(a, 0, i + 1, 0, m);
            for (j=i+1;j<n-1;++j) {
                int c2 = minimumArea(a, i + 1, j + 1, 0, m);
                int c3 = minimumArea(a, j + 1, n, 0, m);
                res = min(res, c1+c2+c3);
            }
            for (j=0;j<m-1;++j) {
                int c2 = minimumArea(a, i + 1, n, 0, j+1);
                int c3 = minimumArea(a, i + 1, n, j+1, m);
                res = min(res, c1+c2+c3);

            }
        }
        return res;

    }
    int minimumSum(vector<vector<int>>& a) {
        int i,j,k;
        int n = a.size();
        int m = a[0].size();
        int res = n * m;
        for (k=0;k<4;++k) {
            res = min(res, check(a));
            reverse(a);
        }
        return res;
    }
};
