class Solution {
public:
    int help(vector<vector<int>>& grid,int top,int down,int left,int right){
        int ntop=-1;int ndown=-1;int nleft=-1;int nright=-1;
        for(int i=top;i<=down;i++){
            for(int j=left;j<=right;j++){
                if(grid[i][j]==1){
                    if(ntop==-1||i<ntop) ntop=i;
                    if(nleft==-1||nleft>j) nleft=j;
                    if(nright==-1||j>nright) nright=j;
                    if(ndown==-1||ndown<i) ndown=i;
                }
            }
        }
        if(ntop==-1) return 0;
        else  return (ndown-ntop+1)*(nright-nleft+1);
    }
    int minimumSum(vector<vector<int>>& grid) {
        int ans=INT_MAX;
        int n=grid.size();int m=grid[0].size();
        for(int r=0;r<n-1;r++){
            for(int c=0;c<m-1;c++){
                int a1=help(grid,0,r,0,c);
                int a2=help(grid,0,r,c+1,m-1);
                int a3=help(grid,r+1,n-1,0,m-1);
                if(a1!=0&&a2!=0&&a3!=0) ans=min(ans,a1+a2+a3);
            }
            for(int c=0;c<m-1;c++){
                int a1=help(grid,0,r,0,m-1);
                int a2=help(grid,r+1,n-1,0,c);
                int a3=help(grid,r+1,n-1,c+1,m-1);
                if(a1!=0&&a2!=0&&a3!=0) ans=min(ans,a1+a2+a3);
            }
        }
        for(int c=0;c<m-1;c++){
            for(int r=0;r<n-1;r++){
                int a1=help(grid,0,r,0,c);
                int a2=help(grid,r+1,n-1,0,c);
                int a3=help(grid,0,n-1,c+1,m-1);
                if(a1!=0&&a2!=0&&a3!=0) ans=min(ans,a1+a2+a3);
            }
            for(int r=0;r<n-1;r++){
                int a1=help(grid,0,n-1,0,c);
                int a2=help(grid,0,r,c+1,m-1);
                int a3=help(grid,r+1,n-1,c+1,m-1);
                if(a1!=0&&a2!=0&&a3!=0) ans=min(ans,a1+a2+a3);
            }
        }
        for(int r=0;r<n-2;r++){
            for(int nr=r+1;nr<n-1;nr++){
                int a1=help(grid,0,r,0,m-1);
                int a2=help(grid,r+1,nr,0,m-1);
                int a3=help(grid,nr+1,n-1,0,m-1);
                if(a1!=0&&a2!=0&&a3!=0) ans=min(ans,a1+a2+a3);
            }
        }
        for(int c=0;c<m-2;c++){
            for(int nc=c+1;nc<m-1;nc++){
                int a1=help(grid,0,n-1,0,c);
                int a2=help(grid,0,n-1,c+1,nc);
                int a3=help(grid,0,n-1,nc+1,m-1);
                if(a1!=0&&a2!=0&&a3!=0) ans=min(ans,a1+a2+a3);
            }
        }
        return ans;
    }
};
