class Solution {
private:
     int minimumArea(vector<vector<int>>& grid,int x1,int y1,int x2,int y2){
        int li[4] = {1000,0,1000,0};
        for(int i = x1;i < x2;i ++)
        {
            for(int j = y1;j < y2;j ++)
            {
                int t = grid[i][j];
                if(!t)continue;
                li[0] = min(li[0],i);
                li[1] = max(li[1],i);
                li[2] = min(li[2],j);
                li[3] = max(li[3],j);

            }
        }
        if(li[0] == 1000 || li[2] == 1000)return -1;
        return (li[1] - li[0] + 1) * (li[3] - li[2] + 1);
    }
    public:
    int minimumSum(vector<vector<int>>& grid) {

        int n = grid.size(),m = grid[0].size();
        int ans = 1e9;
        for(int i = 1;i < n;i ++){
            for(int j = 1;j < m;j ++){
                int res = minimumArea(grid,0,0,i,j);
                if(res == -1)continue;
                int res1 = minimumArea(grid,i,0,n,j);
                if(res1 == -1)continue;
                int res2 = minimumArea(grid,0,j,n,m);
                if(res2 == -1)continue;
                ans = min(ans,res+res1+res2);
                res1 = minimumArea(grid,0,j,i,m);
                if(res1 == -1)continue;
                res2 = minimumArea(grid,i,0,n,m);
                if(res2 == -1)continue;
                ans = min(ans,res+res1+res2);
            }
        }
        for(int i = 1;i < n;i ++){
            int res = minimumArea(grid,0,0,i,m);
            if(res == -1)continue;
            for(int j = i;j < n;j ++){
                int res1 = minimumArea(grid,i,0,j,m);
                if(res1 == -1)continue;
                int res2 = minimumArea(grid,j,0,n,m);
                if(res2 == -1)continue;
                ans = min(ans,res+res1+res2);
            }
            for(int j = 1;j < m;j ++){
                int res1 = minimumArea(grid,i,0,n,j);
                if(res1 == -1)continue;
                int res2 = minimumArea(grid,i,j,n,m);
                if(res2 == -1)continue;
                ans = min(ans,res+res1+res2);
            }
        }
        for(int i = 1;i < m;i ++){
            int res = minimumArea(grid,0,0,n,i);
            if(res == -1)continue;
            for(int j = i;j < m;j ++){
                int res1 = minimumArea(grid,0,i,n,j);
                if(res1 == -1)continue;
                int res2 = minimumArea(grid,0,j,n,m);
                if(res2 == -1)continue;
                ans = min(ans,res+res1+res2);
            }
            for(int j = 1;j < n;j ++){
                int res1 = minimumArea(grid,0,i,j,m);
                if(res1 == -1)continue;
                int res2 = minimumArea(grid,j,i,n,m);
                if(res2 == -1)continue;
                ans = min(ans,res+res1+res2);
            }
        }
        return ans;
    }
};
