class Solution {
public:
    int getS(vector<vector<int>>& grid, int t1, int t2, int b1, int b2){
        int x1 = 1000, y1 = 1000, x2 = 0, y2 = 0;

        bool flag = false;
        for(int i = t1; i <= b1; ++i){
            for(int j = t2; j <= b2; ++j){
                if(grid[i][j]){
                    flag = true;
                    x1 = min(x1, i);
                    x2 = max(x2, i);
                    y1 = min(y1, j);
                    y2 = max(y2, j);
                }
            }
        }

        if(!flag){
            return 0;
        }

        return (abs(x2 - x1) + 1) * (abs(y2 - y1) + 1);
    }
    int minimumSum(vector<vector<int>>& grid) {
        int x1 = 1000, y1 = 1000, x2 = 0, y2 = 0;
        int m = grid.size();
        int n = grid[0].size();

        for(int i = 0; i < m; ++i){
            for(int j = 0; j < n; ++j){
                if(grid[i][j]){
                    x1 = min(x1, i);
                    x2 = max(x2, i);
                    y1 = min(y1, j);
                    y2 = max(y2, j);
                }
            }
        }

        int ans = INT_MAX;
        // 行
        for(int i = x1; i <= x2; ++i){
            for(int j = x1 + 1; j < x2; ++j){
                int tmp = getS(grid, x1, y1, i, y2) + getS(grid, i + 1, y1, j, y2) + getS(grid, j + 1, y1, x2, y2);
                ans = min(ans, tmp);
            }
        }
        // 列
        for(int i = y1; i <= y2; ++i){
            for(int j = y1 + 1; j < y2; ++j){
                int tmp = getS(grid, x1, y1, x2, i) + getS(grid, x1, i + 1, x2, j) + getS(grid, x1, j + 1, x2, y2);
                ans = min(ans, tmp);
            }
        }
        // 行&列
        for(int i = x1; i < x2; ++i){
            for(int j = y1; j < y2; ++j){
                int tmp1 = getS(grid, x1, y1, i, j) + getS(grid, x1, j + 1, i, y2) + getS(grid, i + 1, y1, x2, y2);
                ans = min(ans, tmp1);
                int tmp2 = getS(grid, x1, y1, i, y2) + getS(grid, i + 1, y1, x2, j) + getS(grid, i + 1, j + 1, x2, y2);
                ans = min(ans, tmp2);
                int tmp3 = getS(grid, x1, y1, i, j) + getS(grid, i + 1, y1, x2, j) + getS(grid, x1, j + 1, x2, y2);
                ans = min(ans, tmp3);
                int tmp4 = getS(grid, x1, y1, x2, j) + getS(grid, x1, j + 1, i, y2) + getS(grid, i + 1, j + 1, x2, y2);
                ans = min(ans, tmp4);
            }
        }

        return ans;        
    }
};
