int dp[31][31][31][31][4],dp2[31][31][31][31];
class Solution {
public:
    int calDp2(vector<vector<int>>& grid1,int l1,int r1,int l2,int r2){
        if(l1==l2&&r1==r2){
            return grid1[l1][r1]==1;
        }else if(dp2[l1][r1][l2][r2]!=-1)return dp2[l1][r1][l2][r2];
        else{
            int flag=1;
            for(int i=r1;i<=r2;++i){
                flag&=(grid1[l1][i]==0);
            }
            if(flag&&l1<l2)return dp2[l1][r1][l2][r2]=calDp2(grid1,l1+1,r1,l2,r2);
            flag=1;
            for(int i=r1;i<=r2;++i){
                flag&=(grid1[l2][i]==0);
            }
            if(flag&&l1<l2)return dp2[l1][r1][l2][r2]=calDp2(grid1,l1,r1,l2-1,r2);
            flag=1;
            for(int i=l1;i<=l2;++i){
                flag&=(grid1[i][r1]==0);
            }
            if(flag&&r1<r2)return dp2[l1][r1][l2][r2]=calDp2(grid1,l1,r1+1,l2,r2);
            flag=1;
            for(int i=l1;i<=l2;++i){
                flag&=(grid1[i][r2]==0);
            }
            if(flag&&r1<r2)return dp2[l1][r1][l2][r2]=calDp2(grid1,l1,r1,l2,r2-1);
            return dp2[l1][r1][l2][r2]=(r2-r1+1)*(l2-l1+1);
        }
    }
    int calDp1(vector<vector<int>>& grid1,int k,int l1,int r1,int l2,int r2){
        // cout<<k<<" "<<l1<<" "<<r1<<" "<<l2<<" "<<r2<<endl;
        if(k==1){
            // cout<<l1<<" "<<r1<<" "<<l2<<" "<<r2<<endl;
            return calDp2(grid1,l1,r1,l2,r2);
        }else if(dp[l1][r1][l2][r2][k]!=-1)return dp[l1][r1][l2][r2][k];
        else{
            int mini=1000;
            for(int i=l1;i<=l2;++i){
                if(i!=l2)mini=min(mini,calDp1(grid1,1,l1,r1,i,r2)+calDp1(grid1,k-1,i+1,r1,l2,r2));
                if(i!=l2)mini=min(mini,calDp1(grid1,k-1,l1,r1,i,r2)+calDp1(grid1,1,i+1,r1,l2,r2));
            }
            for(int i=r1;i<=r2;++i){
                if(i!=r2)mini=min(mini,calDp1(grid1,1,l1,r1,l2,i)+calDp1(grid1,k-1,l1,i+1,l2,r2));
                if(i!=r2)mini=min(mini,calDp1(grid1,k-1,l1,r1,l2,i)+calDp1(grid1,1,l1,i+1,l2,r2));
            }
            return dp[l1][r1][l2][r2][k]=mini;
        }
    }
    int minimumSum(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        memset(dp,-1,sizeof(dp));
        memset(dp2,-1,sizeof(dp2));
        //////
        // for(int i=0;i<n;++i){
        //     for(int j=0;j<m;++j){
        //         cout<<calDp2(grid,0,0,i,j)<<" ";
        //     }
        //     cout<<endl;
        // }
        return calDp1(grid,3,0,0,n-1,m-1);
    }
};
