class Solution {
public:
    vector<int> findmax(int i1,int m1,int j1,int n1,vector<vector<int>>& grid){
        int m=grid.size(),n=grid[0].size();
        int l=INT_MAX,r=INT_MIN,t=INT_MAX,d=INT_MIN;
        for(int i=i1;i<m1;i++){
            for(int j=j1;j<n1;j++){
                if(grid[i][j]==1){
                    l=min(l,j);
                    r=max(r,j+1);
                    t=min(t,i);
                    d=max(d,i+1);
                }
            }
        }
        if(l==INT_MAX || r==INT_MIN ||t==INT_MAX || d==INT_MIN) return {0,0,0,0};
        return {l,r,t,d};
    }
    int rec(vector<int> v){
        return (v[1]-v[0])*(v[3]-v[2]);
    }
    int hormin(int i,int m,vector<vector<int>>& grid){
        int n=grid[0].size();
         int x=INT_MAX;
         for(int j=0;j<n-1;j++){
                   vector<int> v1=findmax(i+1,m,0,j+1,grid),v2=findmax(i+1,m,j+1,n,grid);
                    x=min(x,rec(v1)+rec(v2));
         }
        return x;

    }
    int vertmin(int i,int n,vector<vector<int>>& grid){
        int m=grid.size();
         int x=INT_MAX;
            for(int j=0;j<m-1;j++){
               vector<int> v1=findmax(0,j+1,i+1,n,grid),v2=findmax(j+1,m,i+1,n,grid);
                x=min(x,rec(v1)+rec(v2));
            }
        return x;
    }
    int minimumSum(vector<vector<int>>& grid) {
        //top horizontal part
        int m=grid.size(),n=grid[0].size();
        long long ans=INT_MAX;
        for(int i=0;i<m-1;i++){
            vector<int> v=findmax(0,i+1,0,n,grid);
            long long sol=rec(v)+hormin(i,m,grid);
            if(sol<ans) ans=sol;
            vector<int> v2=findmax(i+1,m,0,n,grid);
            sol=rec(v2)+hormin(-1,i+1,grid);
             if(sol<ans) ans=sol;
        }


         for(int i=0;i<n-1;i++){

            vector<int> v=findmax(0,m,0,i+1,grid);
             long long sol=rec(v)+vertmin(i,n,grid);
            if(sol<ans) ans=sol;
              vector<int> v2=findmax(0,m,i+1,n,grid);
            sol=rec(v2)+vertmin(-1,i+1,grid);
             if(sol<ans) ans=sol;

        }

        //full horizontal

        if(m>=3){
            for(int i=0;i<m-2;i++){
                int x=INT_MAX;
                for(int j=i+1;j<m-1;j++){
                     vector<int> v1=findmax(i+1,j+1,0,n,grid),v2=findmax(j+1,m,0,n,grid);
                     x=min(x,rec(v1)+rec(v2));
                }
                 vector<int> v=findmax(0,i+1,0,n,grid);
                long long sol=rec(v)+x;
                if(sol<ans) ans=sol;
            }
        }
         if(n>=3){
            for(int i=0;i<n-2;i++){
                int x=INT_MAX;
                for(int j=i+1;j<n-1;j++){
                     vector<int> v1=findmax(0,m,i+1,j+1,grid),v2=findmax(0,m,j+1,n,grid);
                     x=min(x,rec(v1)+rec(v2));
                }
                 vector<int> v=findmax(0,m,0,i+1,grid);
                long long sol=rec(v)+x;
                if(sol<ans) ans=sol;
            }
        }

        return ans;
    }
};
