vector<int> f0(vector<vector<int>>& grid, int i0, int i1, int j0, int j1) {
    int n = grid.size(), m = grid[0].size(), x0 = n - 1, x1 = 0, y0 = m - 1, y1 = 0;
    for (int i = i0; i <= i1; i++) {
        for (int j = j0; j <= j1; j++) {
            if (grid[i][j] == 1) {
                x0 = min(x0, i);
                x1 = max(x1, i);
                y0 = min(y0, j);
                y1 = max(y1, j);
            }
        }
    }
    return { x0, x1, y0, y1 };
}

int f(vector<vector<int>>& grid, int i0, int i1, int j0, int j1, int k) {
    int n = grid.size(), m = grid[0].size();
    vector<int> v = f0(grid, i0, i1, j0, j1);
    int x0 = v[0], x1 = v[1], y0 = v[2], y1 = v[3];
    if (k == 1)
        return (x1 - x0 + 1) * (y1 - y0 + 1);
    int ans = 1e9;
    for (int i = x0; i < x1; i++) {
        if (k == 3) {
            ans = min(ans, f(grid, x0, i, y0, y1, 2) + f(grid, i + 1, x1, y0, y1, 1));
            ans = min(ans, f(grid, x0, i, y0, y1, 1) + f(grid, i + 1, x1, y0, y1, 2));
        }
        else
            ans = min(ans, f(grid, x0, i, y0, y1, 1) + f(grid, i + 1, x1, y0, y1, 1));
    }
    for (int j = y0; j < y1; j++) {
        if (k == 3) {
            ans = min(ans, f(grid, x0, x1, y0, j, 2) + f(grid, x0, x1, j + 1, y1, 1));
            ans = min(ans, f(grid, x0, x1, y0, j, 1) + f(grid, x0, x1, j + 1, y1, 2));
        }
        else
            ans = min(ans, f(grid, x0, x1, y0, j, 1) + f(grid, x0, x1, j + 1, y1, 1));
    }
    return ans;
}

class Solution {
public:
    int minimumSum(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        return f(grid, 0, n - 1, 0, m - 1, 3);
    }
};
