class Solution {
public:
    vector<vector<int>> func(vector<vector<int>>& grid) {

        int n=grid.size(),m=grid[0].size();
        int r1=1e9,r2=0,c1=1e9,c2=0;

        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(grid[i][j]==1){
                    r1=min(i,r1);
                    r2=max(i,r2);
                    c1=min(c1,j);
                    c2=max(c2,j);
                }
            }
        }

        int l=r2-r1+1;
        int b=c2-c1+1;
        vector<vector<int>>g(l,vector<int>(b,0));
        for(int i=r1;i<=r2;i++){
            for(int j=c1;j<=c2;j++){
                g[i-r1][j-c1]=grid[i][j];
            }
        }

        return grid;
    }


    int func1(int r1,int c1,int r2,int c2,vector<vector<int>>&grid){



        int x1=1e9,x2=0,y1=1e9,y2=0;
        for(int i=r1;i<=r2;i++){
            for(int j=c1;j<=c2;j++){
                if(grid[i][j]==1){
                    x1=min(x1,i);
                    x2=max(x2,i);
                    y1=min(y1,j);
                    y2=max(y2,j);
                }
            }
        }

        if(x1>x2||y1>y2)return 0;

        int l=x2-x1+1;
        int b=y2-y1+1;
        return l*b;
    }


    vector<vector<int>>rotate(vector<vector<int>>&grid){
        int n=grid.size();
        int m=grid[0].size();

        vector<vector<int>>v(m,vector<int>(n,0));

        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                v[j][n-1-i]=grid[i][j];
            }
        }

        return v;
    }


    int calc(vector<vector<int>>&grid){
        int n=grid.size();
        int m=grid[0].size();

        int ans=1e9;

        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){

                int curans=func1(0,0,i,j,grid)+func1(0,j+1,i,m-1,grid)+func1(i+1,0,n-1,m-1,grid);
                ans=min(ans,curans);
            }
        }

        for(int i=0;i<n;i++){

            for(int j=i+1;j<n;j++){
                int curans=func1(0,0,i,m-1,grid)+func1(i+1,0,j,m-1,grid)+func1(j+1,0,n-1,m-1,grid);
                //cout<<i<<" "<<j<<" "<<curans<<endl;
                ans=min(ans,curans);
            }

            for(int j=0;j<m;j++){
                int curans=func1(0,0,i,m-1,grid)+func1(i+1,0,n-1,j,grid)+func1(i+1,j+1,n-1,m-1,grid);
                ans=min(ans,curans);
            }
        }

        return ans;
    }

    int minimumSum(vector<vector<int>>& grid) {

        grid=func(grid);


        int ans=1e9;
        ans=calc(grid);

        grid=rotate(grid);
        ans=min(ans,calc(grid));

        grid=rotate(grid);
        ans=min(ans,calc(grid));

        grid=rotate(grid);
        ans=min(ans,calc(grid));

        //   for(int i=0;i<grid.size();i++){
        //     for(int j=0;j<grid[0].size();j++)cout<<grid[i][j]<<" "; cout<<endl;
        // }
        grid=rotate(grid);


        return ans;
    }
};
