class Solution {
public:
    int minimumSum(vector<vector<int>>& v) {
        int n = (int) v.size();
        int m = (int) v[0].size();

        int dp[n][m][n][m];
        memset(dp, -1, sizeof(dp));

        auto get = [&] (int sx, int sy, int ex, int ey) {
            if (dp[sx][sy][ex][ey] != -1) {
                return dp[sx][sy][ex][ey];
            }
            int a = 1e9, b = 1e9;
            int c = -1, d = -1;
            for (int i = sx; i <= ex; i++) {
                for (int j = sy; j <= ey; j++) {
                    if (v[i][j]) {
                        a = min(a, i);
                        b = min(b, j);
                        c = max(c, i);
                        d = max(d, j);
                    }
                }
            }
            return dp[sx][sy][ex][ey] = max(0, c - a + 1) * max(0, d - b + 1);
        };

        int ans = n * m;
        for (int i = 0; i + 1 < n; i++) {
            for (int j = i + 1; j + 1 < n; j++) {
                ans = min(ans, get(0, 0, i, m - 1) + get(i + 1, 0, j, m - 1) + get(j + 1, 0, n - 1, m - 1));
            }
            for (int j = 0; j + 1 < m; j++) {
                ans = min(ans, get(0, 0, i, m - 1) + get(i + 1, 0, n - 1, j) + get(i + 1, j + 1, n - 1, m - 1));
            }
        }
        for (int i = 0; i + 1 < m; i++) {
            for (int j = i + 1; j + 1 < m; j++) {
                ans = min(ans, get(0, 0, n - 1, i) + get(0, i + 1, n - 1, j) + get(0, j + 1, n - 1, m - 1));
            }
            for (int j = 0; j + 1 < n; j++) {
                ans = min(ans, get(0, 0, n - 1, i) + get(0, i + 1, j, m - 1) + get(j + 1, i + 1, n - 1, m - 1));
            }
        }

        for (int i = 1; i < n; i++) {
            for (int j = 0; j + 1 < m; j++) {
                ans = min(ans, get(0, 0, i - 1, j) + get(0, j + 1, i - 1, m - 1) + get(i, 0, n - 1, m - 1));
            }
        }

        for (int j = 1; j < m; j++) {
            for (int i = 0; i + 1 < n; i++) {
                ans = min(ans, get(0, 0, i, j - 1) + get(i + 1, 0, n - 1, j - 1) + get(0, j, n - 1, m - 1));
            }
        }

        return ans;
    }
};
