class Solution {
    public int minimumArea(int[][] grid,int minr,int maxr,int minc,int maxc) {
        int minh=maxr+1,minw=maxc+1,maxh=minr-1,maxw=minc-1;
        for(int i=minr;i<=maxr;i++)
            for(int j=minc;j<=maxc;j++)
                if(grid[i][j]==1) {
                    minh=Math.min(minh,i);
                    maxh=Math.max(maxh,i);
                    minw=Math.min(minw,j);
                    maxw=Math.max(maxw,j);
                }
        if(minh==maxr+1 || minw==maxc+1 || maxh==minr-1 || maxw==minc-1)
            return -1;
        return (maxh-minh+1)*(maxw-minw+1);
    }


    public int minimumSum(int[][] grid) {
        int n=grid.length,m=grid[0].length;
        int ans=n*m;
        if(n>2) {
            for(int i=0;i<=n-3;i++)
                for(int j=i+1;j<=n-2;j++) {
                    int v1=minimumArea(grid,0,i,0,m-1);
                    int v2=minimumArea(grid,i+1,j,0,m-1);
                    int v3=minimumArea(grid,j+1,n-1,0,m-1);
                    if(v1!=-1 && v2!=-1 && v3!=-1)
                        ans=Math.min(ans,v1+v2+v3);
                }
        }
        if(m>2) {
            for(int i=0;i<=m-3;i++)
                for(int j=i+1;j<=m-2;j++) {
                    int v1=minimumArea(grid,0,n-1,0,i);
                    int v2=minimumArea(grid,0,n-1,i+1,j);
                    int v3=minimumArea(grid,0,n-1,j+1,m-1);
                    if(v1!=-1 && v2!=-1 && v3!=-1)
                        ans=Math.min(ans,v1+v2+v3);
                }
        }
        if(n>=2 && m>=2) {
            for(int i=0;i<=n-2;i++)
                for(int j=0;j<=m-2;j++) {
                    int v1=minimumArea(grid,0,i,0,m-1);
                    int v2=minimumArea(grid,i+1,n-1,0,j);
                    int v3=minimumArea(grid,i+1,n-1,j+1,m-1);
                    if(v1!=-1 && v2!=-1 && v3!=-1)
                        ans=Math.min(ans,v1+v2+v3);
                }

            for(int i=0;i<=n-2;i++)
                for(int j=0;j<=m-2;j++) {
                    int v1=minimumArea(grid,0,i,0,j);
                    int v2=minimumArea(grid,0,i,j+1,m-1);
                    int v3=minimumArea(grid,i+1,n-1,0,m-1);
                    if(v1!=-1 && v2!=-1 && v3!=-1)
                        ans=Math.min(ans,v1+v2+v3);
                }

            for(int j=0;j<=m-2;j++)
                for(int i=0;i<=n-2;i++) {
                    int v1=minimumArea(grid,0,n-1,0,j);
                    int v2=minimumArea(grid,0,i,j+1,m-1);
                    int v3=minimumArea(grid,i+1,n-1,j+1,m-1);
                    if(v1!=-1 && v2!=-1 && v3!=-1)
                        ans=Math.min(ans,v1+v2+v3);
                }

            for(int j=0;j<=m-2;j++)
                for(int i=0;i<=n-2;i++) {
                    int v1=minimumArea(grid,0,i,0,j);
                    int v2=minimumArea(grid,i+1,n-1,0,j);
                    int v3=minimumArea(grid,0,n-1,j+1,m-1);
                    if(v1!=-1 && v2!=-1 && v3!=-1)
                        ans=Math.min(ans,v1+v2+v3);
                }
        }

        return ans;
    }
}
