class Solution {
public:
    int cal(int x1,int x2,int y1,int y2,vector<vector<int>>& grid){
    int mx=30,my=30,ax=0,ay=0;
    bool has=false;
    for(int i=y1;i<=y2;i++){
        for(int j=x1;j<=x2;j++){
            if(grid[i][j]==1){
                has=true;
                mx=min(mx,j);
                my=min(my,i);
                ax=max(ax,j);
                ay=max(ay,i);
            }
        }
    }
    if(has){
        return (ax-mx+1)*(ay-my+1);
    }else{
        return 0;
    }
}
int minimumSum(vector<vector<int>>& grid) {
    int y=grid.size(),x=grid[0].size();
    int ans=x*y;
    for(int i=0;i<y;i++){
        for(int j=i+2;j<y;j++){
            ans=min(ans,cal(0,x-1,0,i,grid)+cal(0,x-1,i+1,j-1,grid)+cal(0,x-1,j,y-1,grid));
        }
    }
    for(int i=0;i<x;i++){
        for(int j=i+2;j<x;j++){
            ans=min(ans,cal(0,i,0,y-1,grid)+cal(i+1,j-1,0,y-1,grid)+cal(j,x-1,0,y-1,grid));
        }
    }
    for(int i=0;i<x-1;i++){
        for(int j=0;j<y-1;j++){
            ans=min(ans,cal(0,i,0,y-1,grid)+cal(i+1,x-1,0,j,grid)+cal(i+1,x-1,j+1,y-1,grid));
            ans=min(ans,cal(0,i,0,j,grid)+cal(0,i,j+1,y-1,grid)+cal(i+1,x-1,0,y-1,grid));
        }
    }
    for(int i=0;i<y-1;i++){
        for(int j=0;j<x-1;j++){
            ans=min(ans,cal(0,x-1,0,i,grid)+cal(0,j,i+1,y-1,grid)+cal(j+1,x-1,i+1,y-1,grid));
            ans=min(ans,cal(0,j,0,i,grid)+cal(j+1,x-1,0,i,grid)+cal(0,x-1,i+1,y-1,grid));
        }
    }
    return ans;
}
