int doit(vector<vector<int>>& a,int x1,int y1,int x2,int y2)
{
    int i,j,xx1,yy1,xx2,yy2,cnt;
    xx1=(1<<30);
    yy1=(1<<30);
    xx2=-(1<<30);
    yy2=-(1<<30);
    cnt=0;
    for (i=x1;i<=x2;i++)
        for (j=y1;j<=y2;j++)
        {
            if (a[i][j]==0) continue;
            cnt++;
            xx1=min(xx1,i);
            yy1=min(yy1,j);
            xx2=max(xx2,i);
            yy2=max(yy2,j);
        }
    if (cnt==0) return -1;
    return (xx2-xx1+1)*(yy2-yy1+1);
}


class Solution {
public:
    int minimumSum(vector<vector<int>>& a) {
        int i,j,m,n,x1,y1,x2,y2,v1,v2,v3,ans;
        m=a.size();
        n=a[0].size();
        ans=(1<<30);
        for (i=0;i+1<m;i++)
            for (j=i+1;j+1<m;j++)
            {
                v1=doit(a,0,0,i,n-1);
                v2=doit(a,i+1,0,j,n-1);
                v3=doit(a,j+1,0,m-1,n-1);
                if ((v1==-1)||(v2==-1)||(v3==-1)) continue;
                ans=min(ans,v1+v2+v3);
            }
        //cout<<"ans1:"<<ans<<endl;
        for (i=0;i+1<n;i++)
            for (j=i+1;j+1<n;j++)
            {
                v1=doit(a,0,0,m-1,i);
                v2=doit(a,0,i+1,m-1,j);
                v3=doit(a,0,j+1,m-1,n-1);
                if ((v1==-1)||(v2==-1)||(v3==-1)) continue;
                ans=min(ans,v1+v2+v3);
            }
        //cout<<"ans2:"<<ans<<endl;

        for (i=0;i+1<m;i++)
            for (j=0;j+1<n;j++)
            {
                v1=doit(a,0,0,i,n-1);
                v2=doit(a,i+1,0,m-1,j);
                v3=doit(a,i+1,j+1,m-1,n-1);
                if ((v1!=-1)&&(v2!=-1)&&(v3!=-1)) ans=min(ans,v1+v2+v3);

                v1=doit(a,0,0,i,j);
                v2=doit(a,0,j+1,i,n-1);
                v3=doit(a,i+1,0,m-1,n-1);
                if ((v1!=-1)&&(v2!=-1)&&(v3!=-1)) ans=min(ans,v1+v2+v3);

                v1=doit(a,0,0,m-1,j);
                v2=doit(a,0,j+1,i,n-1);
                v3=doit(a,i+1,j+1,m-1,n-1);
                if ((v1!=-1)&&(v2!=-1)&&(v3!=-1)) ans=min(ans,v1+v2+v3);

                v1=doit(a,0,0,i,j);
                v2=doit(a,i+1,0,m-1,j);
                v3=doit(a,0,j+1,m-1,n-1);
                if ((v1!=-1)&&(v2!=-1)&&(v3!=-1)) ans=min(ans,v1+v2+v3);
            }
        //cout<<"ans3:"<<ans<<endl;

        return ans;
    }
};
