class Solution {

    int rectangle(int i1, int i2, int j1,int j2 ,vector<vector<int>>& v1 ){
        int l1=1e9+7,h1=0, l2=1e9+7,h2=0;
        for(int i=i1 ; i<i2 ; i++){
            for(int j=j1; j<j2; j++){
                if( v1[i][j]==1){
                    l1=min(l1,i); h1=max(h1,i); l2=min(l2,j); h2=max(h2,j);
                }
            }
        }
        if( l1 ==1e9+7) return 0;
        else return (h1-l1+1)*(h2-l2+1);
    }

    int twopart(int i1, int i2, int j1, int j2, vector<vector<int>>& v1){

        int ans=1e9+7;
        for(int i=i1; i<i2; i++){
            int ans1=rectangle(i1,i,j1,j2,v1)+rectangle(i,i2,j1,j2,v1);
            ans=min(ans,ans1);
        }

        for(int j=j1; j<j2; j++){
            int ans1= rectangle(i1,i2, j1, j,v1)+rectangle( i1, i2, j,j2,v1);
            ans= min(ans,ans1);
        }
        return ans;
    }

