class Solution {
public:
    int minimumSum(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans = 900;
        auto S = [&](int l, int r, int t, int d) -> int {
            int nl = r;
            int nr = l;
            int nt = d;
            int nd = t;
            bool is = false;
            for (int i = t; i <= d; i++) {
                for (int j = l; j <= r; j++) {
                    if (grid[i][j]) {
                        is = true;
                        nl = min(nl, j);
                        nr = max(nr, j);
                        nt = min(nt, i);
                        nd = max(nd, i);
                    }
                }
            }
            return is ? (nr - nl + 1) * (nd - nt + 1) : 0;
        };
        for (int i = 0; i + 1 < n; i++) {
            int s1 = S(0, m - 1, 0, i);
            if (s1 == 0) continue;
            for (int j = i + 1; j + 1 < n; j++) {
                int s2 = S(0, m - 1, i + 1, j);
                if (s2 == 0) continue;
                int s3 = S(0, m - 1, j + 1, n - 1);
                if (s3 == 0) continue;
                // cout << "0 " << i << " " << j << endl; 
                ans = min(ans, s1 + s2 + s3);
            }
            for (int j = 0; j + 1 < m; j++) {
                int s2 = S(0, j, i + 1, n - 1);
                if (s2 == 0) continue;
                int s3 = S(j + 1, m - 1, i + 1, n - 1);
                if (s3 == 0) continue;
                // cout << "1 " << i << " " << j << endl; 
                ans = min(ans, s1 + s2 + s3);
            }
            s1 = S(0, m - 1, i + 1, n - 1);
            if (s1 == 0) continue;
            for (int j = 0; j + 1 < m; j++) {
                int s2 = S(0, j, 0, i);
                if (s2 == 0) continue;
                int s3 = S(j + 1, m - 1, 0, i);
                if (s3 == 0) continue;
                // cout << "2 " << i << " " << j << endl; 
                ans = min(ans, s1 + s2 + s3);
            }
        }
        for (int i = 0; i + 1 < m; i++) {
            int s1 = S(0, i, 0, n - 1);
            if (s1 == 0) continue;
            for (int j = i + 1; j + 1 < m; j++) {
                int s2 = S(i + 1, j, 0, n - 1);
                if (s2 == 0) continue;
                int s3 = S(j + 1, m - 1, 0, n - 1);
                if (s3 == 0) continue;
                // cout << "3 " << i << " " << j << endl; 
                ans = min(ans, s1 + s2 + s3);
            }
            for (int j = 0; j + 1 < n; j++) {
                int s2 = S(i + 1, m - 1, 0, j);
                if (s2 == 0) continue;
                int s3 = S(i + 1, m - 1, j + 1, n - 1);
                if (s3 == 0) continue;
                // cout << "4 " << i << " " << j << endl; 
                ans = min(ans, s1 + s2 + s3);
            }
            s1 = S(i + 1, m - 1, 0, n - 1);
            if (s1 == 0) continue;
            for (int j = 0; j + 1 < n; j++) {
                int s2 = S(0, i, 0, j);
                if (s2 == 0) continue;
                int s3 = S(0, i, j + 1, n - 1);
                if (s3 == 0) continue;
                // cout << "5 " << i << " " << j << endl; 
                ans = min(ans, s1 + s2 + s3);
            }
        }
        return ans;
    }
};
