int ma(vector<vector<int>>& grid, int a, int b, int c, int d2) {
    int u = 1000000, d = -1, l = 10000000, r = -1;
    for (int i = a; i <= b; i++) {
        for (int j = c; j <= d2; j++) {
            if (grid[i][j] == 1) {
                u = min(u, i);
                d = max(d, i);
                l = min(l, j);
                r = max(r, j);
            }
        }
    }
    if (d == -1) return 0;
    return (r - l + 1) * (d - u + 1);
}

class Solution {
public:
    int minimumSum(vector<vector<int>>& grid) {
        int res = 1e9;
        int n = grid.size(), m = grid[0].size();
        int r1, r2, r3;
        auto &g = grid;
        auto cal = [&]()->void {
            res = min(res, r1 + r2 + r3);
        };
        for (int i = 0; i < n - 2; i++) {
            for (int j = i + 1; j < n - 1; j++) {
                r1 = ma(grid, 0, i, 0, m - 1);
                r2 = ma(grid, i + 1, j, 0, m - 1);
                r3 = ma(grid, j + 1, n - 1, 0, m - 1);
                res = min(res, r1 + r2 + r3);
            }
        }
        for (int i = 0; i < m - 2; i++) {
            for (int j = i + 1; j < m - 1; j++) {
                r1 = ma(grid, 0, n - 1, 0, i);
                r2 = ma(grid, 0, n - 1, i + 1, j);
                r3 = ma(grid, 0, n - 1, j + 1, m - 1);
                res = min(res, r1 + r2 + r3);
            }
        }
        for (int i = 0; i < n - 1; i++) {
            r1 = ma(grid, 0, i, 0, m - 1);
            for (int j = 0; j < m - 1; j++) {
                r2 = ma(g, i + 1, n - 1, 0, j);
                r3 = ma(g, i + 1, n - 1, j + 1, m - 1);
                cal();
            }
            r1 = ma(grid, i + 1, n - 1, 0, m - 1);
            for (int j = 0; j < m - 1; j++) {
                r2 = ma(g, 0, i, 0, j);
                r3 = ma(g, 0, i, j + 1, m - 1);
                cal();
            }
        }
        //return res;
        for (int j = 0; j < m - 1; j++) {
            r1 = ma(grid, 0, n - 1, 0, j);
            for (int i = 0; i < n - 1; i++) {
                r2 = ma(g, 0, i, j + 1, m - 1);
                r3 = ma(g, i + 1, n - 1, j + 1, m - 1);
                cal();
            }
            r1 = ma(grid, 0, n - 1, j + 1, m - 1);
            for (int i = 0; i < n - 1; i++) {
                r2 = ma(g, 0, i, 0, j);
                r3 = ma(g, i + 1, n - 1, 0, j);
                cal();
            }
        }



        return res;
    }
};
