class Solution:
    def minimumSum(self, grid: List[List[int]]) -> int:
        m = len(grid)
        n = len(grid[0])
        ans = 999
        for i in range(1,m):
            temp = self.minimumArea2(grid, 0, i, 0, n) + self.minimumArea(grid, i, m, 0, n)
            if temp < ans:
                ans = temp
            temp = self.minimumArea(grid, 0, i, 0, n) + self.minimumArea2(grid, i, m, 0, n)
            if temp < ans:
                ans = temp
        for j in range(1,n):
            temp = self.minimumArea2(grid, 0, m, 0, j) + self.minimumArea(grid, 0, m, j, n)
            if temp < ans:
                ans = temp
            temp = self.minimumArea(grid, 0, m, 0, j) + self.minimumArea2(grid, 0, m, j, n)
            if temp < ans:
                ans = temp
        return ans

    def minimumArea2(self, grid: List[List[int]], a1:int, a2:int, a3:int, a4:int) -> int:
        ans = 999
        for i in range(a1+1,a2):
            temp = self.minimumArea(grid, a1, i, a3, a4) + self.minimumArea(grid, i, a2, a3, a4)
            if temp < ans:
                ans = temp
        for j in range(a3+1,a4):
            temp = self.minimumArea(grid, a1, a2, a3, j) + self.minimumArea(grid, a1, a2, j, a4)
            if temp < ans:
                ans = temp
        return ans


    def minimumArea(self, grid: List[List[int]], a1:int, a2:int, a3:int, a4:int) -> int:
        m = len(grid)
        n = len(grid[0])
        l = n
        r = -1
        t = m
        b = -1

        for i in range(a1, a2):
            for j in range(a3, a4):
                if grid[i][j]:
                    if i < t:
                        t = i
                    if i > b:
                        b = i
                    if j < l:
                        l = j
                    if j > r:
                        r = j
        if l > r:
            return 1
        return (r-l+1)*(b-t+1)
