class Solution {
public:
    int minimumSum(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        auto area = [&](int x1, int y1, int x2, int y2) -> int {
            int t = x2, b = x1, l = y2, r = y1;
            for (int i = x1; i <= x2; i++) {
                for (int j = y1; j <= y2; j++) {
                    if (grid[i][j]) {
                        t = min(t, i);
                        b = max(b, i);
                        l = min(l, j);
                        r = max(r, j);
                    }
                }
            }
            return max(0, (r - l + 1) * (b - t + 1));
        };
        int ans = m * n;
        for (int i = 0; i < n; i++) {
            int a1 = area(0, 0, i, m - 1);
            for (int j = i + 1; j < n; j++) {
                int a2 = area(i + 1, 0, j, m - 1);
                int a3 = area(j + 1, 0, n - 1, m - 1);
                ans = min(ans, a1 + a2 + a3);
            }
            for (int j = 0; j < m; j++) {
                int b1 = area(i + 1, 0, n - 1, j);
                int b2 = area(i + 1, j + 1, n - 1, m - 1);
                ans = min(ans, a1 + b1 + b2);
            }
            int a11 = area(i + 1, 0, n - 1, m - 1);
            for (int j = 0; j < m; j++) {
                int b1 = area(0, 0, i, j);
                int b2 = area(0, j + 1, i, m - 1);
                ans = min(ans, a11 + b1 + b2);
            }
        }
        for (int j = 0; j < m; j++) {
            int b1 = area(0, 0, n - 1, j);
            for (int i = j + 1; i < m; i++) {
                int b2 = area(0, j + 1, n - 1, i);
                int b3 = area(0, i + 1, n - 1, m - 1);
                ans = min(ans, b1 + b2 + b3);
            }
            for (int i = 0; i < n; i++) {
                int a1 = area(0, j + 1, i, m - 1);
                int a2 = area(i + 1, j + 1, n - 1, m - 1);
                ans = min(ans, b1 + a1 + a2);
            }
            int b11 = area(0, j + 1, n - 1, m - 1);
            for (int i = 0; i < n; i++) {
                int a1 = area(0, 0, i, j);
                int a2 = area(i + 1, 0, n - 1, j);
                ans = min(ans, b11 + a1 + a2);
            }
        }
        cout << "\n\n";
        return ans;
    }
};
