class Solution {
public:
    int minimumSum(vector<vector<int>>& grid) {
        vector<vector<int>> data = grid;
        for(size_t i=0;i<grid.size();++i) {
            for(size_t j=0;j<grid[0].size();++j) {
                if(i > 0) {
                    data[i][j] += data[i-1][j];
                }
                if(j > 0) {
                    data[i][j] += data[i][j-1];
                }
                if(i > 0 && j > 0) {
                    data[i][j] -= data[i-1][j-1];
                }
            }
        }
        int result = INT_MAX;
        for(size_t i=0;i<grid.size();++i) {
            for(size_t j=0;j<grid[0].size();++j) {
                int num = INT_MAX;
                if(i == grid.size() - 1 || data[i][j] == data[grid.size()-1][j]) {
                    for(size_t i1=0;i1<grid.size() - 1;++i1) {
                        num = min(num,h(grid,0,i1,j+1,grid[0].size()-1) + h(grid,i1+1,grid.size()-1,j+1,grid[0].size()-1));
                    }
                    for(size_t j1=j+1;j1<grid[0].size() - 1;++j1) {
                        num = min(num,h(grid,0,grid.size()-1,j+1,j1) + h(grid,0,grid.size()-1,j1+1,grid[0].size()-1));
                    }
                } else if(j == grid[0].size() - 1 || data[i][j] == data[i][grid[0].size()-1]) {
                    for(size_t i1=i+1;i1<grid.size() - 1;++i1) {
                        num = min(num,h(grid,i+1,i1,0,grid[0].size()-1) + h(grid,i1+1,grid.size()-1,0,grid[0].size()-1));
                    }
                    for(size_t j1=0;j1<grid[0].size() - 1;++j1) {
                        num = min(num,h(grid,i+1,grid.size()-1,0,j1) + h(grid,i+1,grid.size()-1,j1+1,grid[0].size()-1));
                    }
                } else {
                    size_t i1 = i+1;
                    for(;i1<grid.size();++i1) {
                        if(data[i1][j] != data[i][j]) {
                            break;
                        }
                    }
                    size_t j1 = j+1;
                    for(;j1<grid[0].size();++j1) {
                        if(data[i][j1] != data[i][j]) {
                            break;
                        }
                    }
                    //cout<<i1<<"---"<<j1<<endl;
                    num = min(num,h(grid,i1,grid.size()-1,0,grid[0].size()-1) + h(grid,0,i1-1,j+1,grid[0].size()-1));
                    num = min(num,h(grid,i+1,grid.size()-1,0,j1-1) + h(grid,0,grid.size()-1,j1,grid[0].size()-1));
                }
                result = min(result,num + h(grid,0,i,0,j));
            }
        }
        return result;
    }

    int h(vector<vector<int>>& grid,size_t starti,size_t endi,size_t startj,size_t endj) {
        if(starti > endi || startj > endj) {
            return 0;
        }
        size_t mini=grid.size();
        size_t minj=grid[0].size();
        size_t maxi=0;
        size_t maxj=0;
        for(size_t i=starti;i<=endi;++i) {
            for(size_t j=startj;j<=endj;++j) {
                if(grid[i][j] == 1 && mini == grid.size()) {
                    mini = i;
                }
                if(grid[i][j] == 1) {
                    maxi = i;
                }
            }
        }
        for(size_t j=startj;j<=endj;++j) {
            for(size_t i=starti;i<=endi;++i) {
                if(grid[i][j] == 1 && minj == grid[0].size()) {
                    minj = j;
                }
                if(grid[i][j] == 1) {
                    maxj = j;
                }
            }
        }
        //cout<<starti<<"--"<<endi<<"--"<<startj<<"--"<<endj<<endl;
        //cout<<maxi<<"--"<<mini<<"--"<<maxj<<"--"<<minj<<endl;
        return (maxi - mini + 1) * (maxj - minj + 1);
    }
};
