class Solution:
    def minimumSum(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])

        @functools.cache
        def cost(y1, x1, y2, x2):
            up, bottom, left, right = y2 - 1, y1, x2 - 1, x1
            for i in range(y1, y2):
                for j in range(x1, x2):
                    if grid[i][j] == 1:
                        up = min(up, i)
                        bottom = max(bottom, i)
                        left = min(left, j)
                        right = max(right, j)
            return max((right - left + 1) * (bottom - up + 1), 0)

        area = m * n
        #y, x = 1, 1
        #c = cost(0, 0, y, n)
        #print(c)
        #for k in range(y + 1, m):
        #    area = min(area, c + cost(y, 0, k, n) + cost(k, 0, m, n))
        #    print(k, area)
        #return 0


        for y in range(m):
            for x in range(n):
                if 0 < y < m and 0 < x < n:
                    # [1, 2]
                    area = min(area, cost(0, 0, y, n) + cost(y, 0, m, x) + cost(y, x, m, n))
                    # [2, 1]
                    area = min(area, cost(0, 0, y, x) + cost(0, x, y, n) + cost(y, 0, m, n))
                    # [1, [1, 1]]
                    area = min(area, cost(0, 0, m, x) + cost(0, x, y, n) + cost(y, x, m, n))
                    # [[1, 1], 1]
                    area = min(area, cost(0, 0, y, x) + cost(y, 0, m, x) + cost(0, x, m, n))
                if 0 < x < n:
                    # [1, 1, 1]
                    c = cost(0, 0, m, x)
                    for k in range(x + 1, n):
                        area = min(area, c + cost(0, x, m, k) + cost(0, k, m, n))
                if 0 < y < m:
                    # [[1, 1, 1]]
                    c = cost(0, 0, y, n)
                    for k in range(y + 1, m):
                        area = min(area, c + cost(y, 0, k, n) + cost(k, 0, m, n))
                #print(y, x, area)
        return area
