class Solution {
public:
    int func(int xi,int yi,int xe,int ye,vector<vector<int>>& grid)
    {
        int minx=grid.size(),miny=grid[0].size();
        int maxx=0,maxy=0;
        int flag=0;

        for(int i=xi;i<xe;i++)
        for(int j=yi;j<ye;j++)
        if(grid[i][j])
        {
            flag=1;
            maxx=max(maxx,i);
            maxy=max(maxy,j);
            minx=min(minx,i);
            miny=min(miny,j);
        }

        return (maxx-minx+1)*(maxy-miny+1)*flag;
    }
    int solve(vector<vector<int>>& grid) {

        int ans=grid.size()*grid[0].size();

        for(int i=1;i<grid.size();i++)
        {
            int value=func(0,0,i,grid[0].size(),grid);
            int add=grid.size()*grid[0].size();
            for(int j=1;j<grid[0].size();j++)
            add=min(add,func(i,0,grid.size(),j,grid)+func(i,j,grid.size(),grid[0].size(),grid));

            int first=value+add;

            value=func(i,0,grid.size(),grid[0].size(),grid);
            add=grid.size()*grid[0].size();
            for(int j=1;j<grid[0].size();j++)
            add=min(add,func(0,0,i,j,grid)+func(0,j,i,grid[0].size(),grid));

            int second=value+add;
            ans=min({ans,first,second});


        }
             for(int j=1;j<grid[0].size();j++)
                for(int k=j+1;k<grid[0].size();k++)
                    ans=min(ans,(func(0,0,grid.size(),j,grid)+func(0,j,grid.size(),k,grid)+func(0,k,grid.size(),grid[0].size(),grid)));

        return ans;
    }
    int minimumSum(vector<vector<int>>& grid) {

        vector<vector<int>> ngrid;
        for(int j=0;j<grid[0].size();j++)
        {
            vector<int> temp;
            for(int i=0;i<grid.size();i++)
                temp.push_back(grid[i][j]);

            ngrid.push_back(temp);
        }

        int a=solve(grid),b=solve(ngrid);
        return min(a,b);
    }
};
